// Parser le payload du webhook
const raw = $input.item.json;
const payload = raw?.body || raw?.json || raw;
const data = payload?.data || payload;

// Extraire subscriptionId
const subscriptionId = data?.subscriptionId || data?.id || '';
const tenantId = payload?.tenantId || data?.tenantId || '';

if (!subscriptionId) {
  throw new Error('ID de l\'abonnement requis');
}

if (!tenantId) {
  throw new Error('Tenant ID requis');
}

return {
  json: {
    subscriptionId,
    tenantId,
    renewalDate: new Date().toISOString()
  }
};

// Préparer les données pour le renouvellement
const subscriptionData = $input.item.json[0];
const parseData = $node['02. Parser payload'].json;

// Calculer la nouvelle date de renouvellement
const currentDate = new Date(subscriptionData.date_prochain_renouvellement);
const dureeMois = 1; // Par défaut 1 mois, à adapter selon le plan
const newRenewalDate = new Date(currentDate);
newRenewalDate.setMonth(newRenewalDate.getMonth() + dureeMois);

// Préparer les données pour la facture
const invoice = {
  clientFinalId: subscriptionData.client_final_id,
  subscriptionId: subscriptionData.id,
  montant: subscriptionData.montant_mensuel,
  dateEmission: new Date().toISOString(),
  dateEcheance: newRenewalDate.toISOString(),
  statut: 'en_attente',
  description: `Renouvellement abonnement ${subscriptionData.nom_plan}`
};

return {
  json: {
    subscription: subscriptionData,
    tenantId: parseData.tenantId,
    newRenewalDate: newRenewalDate.toISOString(),
    invoice,
    clientEmail: subscriptionData.email,
    clientName: subscriptionData.nom || subscriptionData.prenom || subscriptionData.raison_sociale || 'Client'
  }
};

// Vérifier le statut de l'abonnement Stripe
const stripeSubscription = $input.item.json;
const prepareData = $node['05. Préparer renouvellement'].json;

const stripeStatus = stripeSubscription.status;
const isActive = stripeStatus === 'active' || stripeStatus === 'trialing';

if (!isActive) {
  throw new Error(`Abonnement Stripe inactif: ${stripeStatus}`);
}

return {
  json: {
    ...prepareData,
    stripeStatus,
    stripeRenewed: true
  }
};
