parameters.jsCode:
// Parser le payload du webhook
const raw = $input.item.json;
const payload = raw?.body || raw?.json || raw;
const data = payload?.data || payload;

// Extraire les données
const subscriptionId = data?.subscriptionId || data?.id || '';
const tenantId = payload?.tenantId || data?.tenantId || '';
const reason = data?.reason || 'Paiement en retard';

if (!subscriptionId) {
  throw new Error('ID de l\'abonnement requis');
}

if (!tenantId) {
  throw new Error('Tenant ID requis');
}

return {
  json: {
    subscriptionId,
    tenantId,
    reason,
    suspendDate: new Date().toISOString()
  }
};

// Préparer les données pour la suspension
const subscriptionData = $input.item.json[0];
const parseData = $node['02. Parser payload'].json;

return {
  json: {
    subscription: subscriptionData,
    tenantId: parseData.tenantId,
    reason: parseData.reason,
    clientId: subscriptionData.client_id,
    clientEmail: subscriptionData.email,
    clientName: subscriptionData.nom || subscriptionData.prenom || subscriptionData.raison_sociale || 'Client',
    idAbonnementStripe: subscriptionData.id_abonnement_stripe,
    planName: subscriptionData.nom_plan
  }
};

// Préparer l'email de notification de suspension
const data = $node['05. Préparer suspension'].json;

const clientName = data.clientName || 'Client';
const clientEmail = data.clientEmail;
const planName = data.planName;
const reason = data.reason;

const htmlTemplate = `<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <style>
    body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
    .container { max-width: 600px; margin: 0 auto; padding: 20px; }
    .header { background: #ffc107; color: #333; padding: 30px; text-align: center; border-radius: 10px 10px 0 0; }
    .content { background: #f9f9f9; padding: 30px; border-radius: 0 0 10px 10px; }
    .warning-box { background: #fff3cd; padding: 20px; margin: 20px 0; border-radius: 8px; border-left: 4px solid #ffc107; }
    .action-box { background: white; padding: 20px; margin: 20px 0; border-radius: 8px; text-align: center; }
    .button { display: inline-block; padding: 12px 30px; background: #667eea; color: white; text-decoration: none; border-radius: 5px; margin: 10px 0; }
    .footer { text-align: center; margin-top: 30px; color: #666; font-size: 12px; }
  </style>
</head>
<body>
  <div class="container">
    <div class="header">
      <h1>⚠️ Votre abonnement a été suspendu</h1>
    </div>
    <div class="content">
      <p>Bonjour ${clientName},</p>
      <p>Nous vous informons que votre abonnement TalosPrimes a été temporairement suspendu.</p>
      <div class="warning-box">
        <h3>Raison de la suspension :</h3>
        <p>${reason}</p>
      </div>
      <p><strong>Votre accès aux services TalosPrimes est temporairement désactivé.</strong></p>
      <div class="action-box">
        <h3>Comment réactiver votre abonnement ?</h3>
        <p>Pour réactiver votre abonnement, veuillez :</p>
        <ul style="text-align: left; display: inline-block;">
          <li>Régulariser votre paiement</li>
          <li>Contacter notre support si nécessaire</li>
        </ul>
        <p>
          <a href="https://talosprimes.com/support" class="button">Contacter le support</a>
        </p>
      </div>
      <p>Si vous pensez qu'il s'agit d'une erreur, n'hésitez pas à nous contacter.</p>
      <p>Cordialement,<br>L'équipe TalosPrimes</p>
    </div>
    <div class="footer">
      <p>Cet email a été envoyé automatiquement.</p>
    </div>
  </div>
</body>
</html>`;

return {
  json: {
    to: clientEmail,
    subject: '⚠️ Votre abonnement TalosPrimes a été suspendu',
    html: htmlTemplate
  }
};

parameters.jsonBody:
={{ JSON.stringify({
  type: 'subscription_suspended',
  titre: 'Abonnement suspendu',
  message: `L'abonnement de ${$node['05. Préparer suspension'].json.clientName} a été suspendu pour : ${$node['05. Préparer suspension'].json.reason}`,
  donnees: {
    tenantId: $node['05. Préparer suspension'].json.tenantId,
    subscriptionId: $node['05. Préparer suspension'].json.subscription.id,
    clientId: $node['05. Préparer suspension'].json.clientId
  }
}) }}
