// Parser le payload du webhook
const raw = $input.item.json;
const payload = raw?.body || raw?.json || raw;
const data = payload?.data || payload;

// Extraire les données
const subscriptionId = data?.subscriptionId || data?.id || '';
const tenantId = payload?.tenantId || data?.tenantId || '';
const nouveauPlan = data?.nouveauPlan || {};

if (!subscriptionId) {
  throw new Error('ID de l\'abonnement requis');
}

if (!tenantId) {
  throw new Error('Tenant ID requis');
}

if (!nouveauPlan.nomPlan || !nouveauPlan.montantMensuel) {
  throw new Error('Nouveau plan incomplet (nomPlan et montantMensuel requis)');
}

return {
  json: {
    subscriptionId,
    tenantId,
    nouveauPlan: {
      nomPlan: nouveauPlan.nomPlan,
      montantMensuel: parseFloat(nouveauPlan.montantMensuel),
      modulesInclus: nouveauPlan.modulesInclus || [],
      dureeMois: nouveauPlan.dureeMois || 1
    },
    upgradeDate: new Date().toISOString()
  }
};

// Calculer la différence et le prorata
const subscriptionData = $input.item.json[0];
const parseData = $node['02. Parser payload'].json;

const ancienMontant = parseFloat(subscriptionData.montant_mensuel);
const nouveauMontant = parseData.nouveauPlan.montantMensuel;
const difference = nouveauMontant - ancienMontant;
const isUpgrade = difference > 0;

// Calculer le prorata (jours restants)
const today = new Date();
const nextRenewal = new Date(subscriptionData.date_prochain_renouvellement);
const daysRemaining = Math.ceil((nextRenewal - today) / (1000 * 60 * 60 * 24));
const daysInMonth = 30;
const prorataRatio = daysRemaining / daysInMonth;
const prorataAmount = difference * prorataRatio;

return {
  json: {
    subscription: subscriptionData,
    tenantId: parseData.tenantId,
    nouveauPlan: parseData.nouveauPlan,
    ancienPlan: {
      nomPlan: subscriptionData.nom_plan,
      montantMensuel: ancienMontant,
      modulesInclus: subscriptionData.modules_inclus
    },
    difference,
    isUpgrade,
    prorata: {
      daysRemaining,
      ratio: prorataRatio,
      amount: prorataAmount
    },
    clientEmail: subscriptionData.email,
    clientName: subscriptionData.nom || subscriptionData.prenom || subscriptionData.raison_sociale || 'Client',
    idAbonnementStripe: subscriptionData.id_abonnement_stripe
  }
};

// Confirmer la mise à jour Stripe
const stripeResponse = $input.item.json;
const prepareData = $node['05. Calculer prorata'].json;

return {
  json: {
    ...prepareData,
    stripeUpdated: true,
    stripeSubscriptionId: stripeResponse.id
  }
};

// Préparer la requête SQL pour mettre à jour l'abonnement
const data = $input.item.json;
const nouveauPlan = data.nouveauPlan;
const modulesArray = nouveauPlan.modulesInclus.map(m => `'${String(m).replace(/'/g, "''")}'`).join(', ');

const query = `UPDATE client_subscriptions
SET 
  nom_plan = '${nouveauPlan.nomPlan.replace(/'/g, "''")}',
  montant_mensuel = ${nouveauPlan.montantMensuel},
  modules_inclus = ARRAY[${modulesArray}]::text[],
  updated_at = NOW()
WHERE id = '${data.subscription.id}'::uuid
RETURNING *;`;

return {
  json: {
    ...data,
    sqlQuery: query
  }
};

// Préparer l'email de confirmation
const subscriptionUpdated = $input.item.json[0];
const data = $node['10. Préparer SQL'].json;

const clientName = data.clientName || 'Client';
const clientEmail = data.clientEmail;
const ancienPlan = data.ancienPlan.nomPlan;
const nouveauPlan = data.nouveauPlan.nomPlan;
const isUpgrade = data.isUpgrade;
const prorata = data.prorata.amount.toFixed(2);

const changeType = isUpgrade ? 'Upgrade' : 'Downgrade';
const changeIcon = isUpgrade ? '⬆️' : '⬇️';

const htmlTemplate = `<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <style>
    body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
    .container { max-width: 600px; margin: 0 auto; padding: 20px; }
    .header { background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 30px; text-align: center; border-radius: 10px 10px 0 0; }
    .content { background: #f9f9f9; padding: 30px; border-radius: 0 0 10px 10px; }
    .plan-box { background: white; padding: 20px; margin: 20px 0; border-radius: 8px; border-left: 4px solid #667eea; }
    .prorata-box { background: #e7f3ff; padding: 15px; margin: 20px 0; border-radius: 8px; }
    .footer { text-align: center; margin-top: 30px; color: #666; font-size: 12px; }
  </style>
</head>
<body>
  <div class="container">
    <div class="header">
      <h1>${changeIcon} ${changeType} de votre abonnement</h1>
    </div>
    <div class="content">
      <p>Bonjour ${clientName},</p>
      <p>Votre abonnement TalosPrimes a été mis à jour avec succès.</p>
      <div class="plan-box">
        <h3>Changement de plan :</h3>
        <p><strong>Ancien plan :</strong> ${ancienPlan} (${data.ancienPlan.montantMensuel}€/mois)</p>
        <p><strong>Nouveau plan :</strong> ${nouveauPlan} (${data.nouveauPlan.montantMensuel}€/mois)</p>
      </div>
      ${isUpgrade && prorata > 0 ? `<div class="prorata-box">
        <h3>Prorata calculé :</h3>
        <p>Un montant de ${prorata}€ sera facturé pour les ${data.prorata.daysRemaining} jours restants jusqu'au prochain renouvellement.</p>
      </div>` : ''}
      <p>Les nouvelles fonctionnalités sont immédiatement disponibles dans votre espace.</p>
      <p>Cordialement,<br>L'équipe TalosPrimes</p>
    </div>
    <div class="footer">
      <p>Cet email a été envoyé automatiquement.</p>
    </div>
  </div>
</body>
</html>`;

return {
  json: {
    to: clientEmail,
    subject: `${changeType} de votre abonnement TalosPrimes`,
    html: htmlTemplate
  }
};
