// Préparer les données pour l'onboarding
const payload = $input.item.json;

// Extraire les données du client depuis le payload
const clientData = payload.data?.client || payload.data || payload;
const tenantId = payload.tenantId || clientData.tenantId;
const clientId = clientData.clientId || clientData.id;

// Plan : utiliser celui du payload si présent, sinon valeurs par défaut
const planFromPayload = payload.data?.plan || {};
const plan = {
  nomPlan: planFromPayload.nomPlan || "Plan Starter",
  montantMensuel: planFromPayload.montantMensuel || 29.99,
  modulesInclus: planFromPayload.modulesInclus || ["gestion_clients", "facturation", "suivi"],
  dureeMois: planFromPayload.dureeMois || 1
};

// Calculer les dates
const dateDebut = new Date();
const dateProchainRenouvellement = new Date();
dateProchainRenouvellement.setMonth(dateProchainRenouvellement.getMonth() + plan.dureeMois);

return {
  json: {
    tenantId,
    clientId,
    clientData,
    plan,
    dateDebut: dateDebut.toISOString(),
    dateProchainRenouvellement: dateProchainRenouvellement.toISOString(),
    statut: "actif"
  }
};

// Préparer les modules inclus pour la requête SQL
const modules = $json.plan.modulesInclus || [];
const modulesArray = modules.map(m => `'${m.replace(/'/g, "''")}'`).join(', ');

// Construire la requête SQL avec les valeurs
const query = `INSERT INTO client_subscriptions (
  id,
  client_final_id,
  nom_plan,
  date_debut,
  date_prochain_renouvellement,
  montant_mensuel,
  modules_inclus,
  statut,
  updated_at
) VALUES (
  gen_random_uuid(),
  '${$json.clientId}'::uuid,
  '${String($json.plan.nomPlan).replace(/'/g, "''")}',
  '${$json.dateDebut}'::timestamptz,
  '${$json.dateProchainRenouvellement}'::timestamptz,
  ${$json.plan.montantMensuel},
  ARRAY[${modulesArray}]::text[],
  'actif'::subscription_status,
  NOW()
)
RETURNING *;`;

return {
  json: {
    ...$json,
    sqlQuery: query
  }
};

// Préparer la réponse de succès
const subscription = $input.item.json;
const clientData = $('01. Préparer données onboarding').item.json.clientData;

return {
  json: {
    success: true,
    message: "Espace client créé avec succès",
    client: clientData,
    subscription: subscription[0] || subscription,
    modulesActives: subscription[0]?.modules_inclus || subscription.modules_inclus || []
  }
};

