const raw = $input.first().json;
const pj = (x) => { if (typeof x !== 'string') return x; try { return JSON.parse(x); } catch (e) { return null; } };
const isO = (v) => v !== null && typeof v === 'object' && !Array.isArray(v);
const g = (o, k1, k2) => { if (!isO(o)) return null; const v = o[k1]; if (v != null && v !== '') return v; if (k2 && o[k2] != null && o[k2] !== '') return o[k2]; return null; };
let payload = isO(raw) ? raw : {};
if (isO(raw) && raw.body !== undefined) payload = typeof raw.body === 'string' ? pj(raw.body) : raw.body;
if (!isO(payload)) payload = {};
let data = isO(payload.data) ? payload.data : payload;
if (!isO(data)) data = payload;
const invoiceId = g(data, 'invoiceId', 'invoice_id') || g(payload, 'invoiceId', 'invoice_id');
const tenantId = g(data, 'tenantId', 'tenant_id') || g(payload, 'tenantId', 'tenant_id');
const baseUrl = (g(data, 'baseUrl', 'base_url') || g(payload, 'baseUrl', 'base_url') || 'https://api.talosprimes.com').toString().replace(/\/$/, '');
if (!invoiceId) return [{ json: { _error: true, message: 'invoiceId requis' } }];
const lienPdf = `${baseUrl}/api/invoices/${invoiceId}/pdf`;
return [{ json: { invoiceId, tenantId, baseUrl, lienPdf } }];

const res = $input.first().json;
const success = res && (res.success === true || (res.data && res.data.invoice));
return [{ json: success ? { success: true, message: 'lienPdf enregistre', lienPdf: $('02. Parser').first().json.lienPdf } : { success: false, error: res?.error || res?.message || 'Erreur API' } }];
