// Parser le payload du webhook
const raw = $input.first().json;
const payload = (raw && typeof raw === 'object' && 'body' in raw) ? raw.body : raw;
const data = (payload && typeof payload === 'object' && 'data' in payload) ? payload.data : payload;

const leadId = data?.id || data?.leadId ? String(data.id || data.leadId) : '';
const dateEntretien = data?.dateEntretien || data?.date || '';
const heureEntretien = data?.heureEntretien || data?.heure || '';
const typeEntretien = data?.typeEntretien || data?.type || 't√©l√©phonique';

if (!leadId) throw new Error('ID du lead requis');

return [{ json: { leadId, dateEntretien, heureEntretien, typeEntretien } }];

// Extraire les donn√©es du lead depuis la r√©ponse API
const response = $input.first().json;
// La r√©ponse API est: { success: true, data: { lead: {...} } }
const leadData = response.data?.lead || response.data || response;
const parsed = $('Parser payload').first().json;

const leadId = leadData.id || leadData.leadId || parsed.leadId;
const nom = leadData.nom || '';
const prenom = leadData.prenom || '';
const email = leadData.email || '';
const telephone = leadData.telephone || '';
const dateEntretien = parsed.dateEntretien || '';
const heureEntretien = parsed.heureEntretien || '';
const typeEntretien = parsed.typeEntretien || 't√©l√©phonique';

if (!leadId) throw new Error('ID du lead introuvable');
if (!email) throw new Error('Email du lead introuvable');

// G√©n√©rer un lien de planification (√† personnaliser selon votre syst√®me)
const lienPlanification = `https://talosprimes.com/planifier/${leadId}`;

// Formater la date et l'heure si fournies
let dateHeureFormatee = '';
if (dateEntretien && heureEntretien) {
  const date = new Date(`${dateEntretien}T${heureEntretien}`);
  dateHeureFormatee = date.toLocaleString('fr-FR', { 
    weekday: 'long', 
    year: 'numeric', 
    month: 'long', 
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit'
  });
}

return [{ json: { leadId, nom, prenom, email, telephone, dateEntretien, heureEntretien, typeEntretien, dateHeureFormatee, lienPlanification } }];

