// Parser le payload du webhook
const raw = $input.first().json;
const payload = (raw && typeof raw === 'object' && 'body' in raw) ? raw.body : raw;
const data = (payload && typeof payload === 'object' && 'data' in payload) ? payload.data : payload;

const leadId = data?.id || data?.leadId ? String(data.id || data.leadId) : '';
if (!leadId) throw new Error('ID du lead requis');

return [{ json: { leadId } }];

// Extraire les données du lead depuis la réponse API
const lead = $input.first().json;
const leadData = lead.data || lead;

const leadId = leadData.id || leadData.leadId || $('Parser payload').first().json.leadId;
const nom = leadData.nom || '';
const prenom = leadData.prenom || '';
const email = leadData.email || '';
const telephone = leadData.telephone || '';

if (!leadId) throw new Error('ID du lead introuvable');
if (!email) throw new Error('Email du lead introuvable');

// Générer un lien unique pour le questionnaire (à personnaliser selon votre système)
const questionnaireUrl = `https://talosprimes.com/questionnaire/${leadId}`;

return [{ json: { leadId, nom, prenom, email, telephone, questionnaireUrl } }];

// Récupérer les données du lead depuis 'Préparer données' pour les passer à Resend
const leadData = $('Préparer données').first().json;
return [{ json: leadData }];
