// Validation des données reçues
const data = $input.all()[0].json;

const errors = [];

if (!data.nom || typeof data.nom !== 'string' || !data.nom.trim()) {
  errors.push('Le nom est requis');
}

if (!data.prenom || typeof data.prenom !== 'string' || !data.prenom.trim()) {
  errors.push('Le prénom est requis');
}

if (!data.telephone || typeof data.telephone !== 'string' || !data.telephone.trim()) {
  errors.push('Le téléphone est requis');
} else if (!/^[0-9+\s\-()]+$/.test(data.telephone)) {
  errors.push('Format de téléphone invalide');
}

if (!data.email || typeof data.email !== 'string' || !data.email.trim()) {
  errors.push('L\'email est requis');
} else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(data.email)) {
  errors.push('Format d\'email invalide');
}

if (errors.length > 0) {
  throw new Error(`Validation échouée: ${errors.join(', ')}`);
}

return {
  json: {
    ...data,
    validated: true,
    dateReception: new Date().toISOString()
  }
};

// Formater les données pour Resend
const data = $input.all()[0].json;

const htmlBody = `
<!DOCTYPE html>
<html lang="fr">
<head>
  <meta charset="UTF-8">
  <style>
    body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
    .container { max-width: 600px; margin: 0 auto; background: #fff; }
    .header { background: linear-gradient(135deg, #4f46e5 0%, #7c3aed 100%); color: white; padding: 30px; text-align: center; }
    .content { padding: 30px; }
    .info-box { background: #f9fafb; border-left: 4px solid #4f46e5; padding: 15px; margin: 20px 0; }
    .highlight { background: #fef3c7; padding: 15px; border-left: 4px solid #f59e0b; margin: 20px 0; }
    .footer { background: #f9fafb; padding: 20px; text-align: center; color: #6b7280; font-size: 12px; }
  </style>
</head>
<body>
  <div class="container">
    <div class="header">
      <h1>TalosPrimes</h1>
      <p>Gestion d'entreprise automatisée</p>
    </div>
    <div class="content">
      <p><strong>Bonjour ${data.prenom} ${data.nom},</strong></p>
      <p>Nous avons bien reçu votre demande d'inscription sur <strong>TalosPrimes</strong>.</p>
      <div class="info-box">
        <p><strong>Vos informations :</strong></p>
        <ul>
          <li><strong>Nom :</strong> ${data.nom}</li>
          <li><strong>Prénom :</strong> ${data.prenom}</li>
          <li><strong>Email :</strong> ${data.email}</li>
          <li><strong>Téléphone :</strong> ${data.telephone}</li>
        </ul>
      </div>
      <div class="highlight">
        <p>Notre équipe va examiner votre demande et vous recontactera dans un délai de <strong>24 à 48 heures</strong>.</p>
      </div>
      <p>Cordialement,<br><strong>L'équipe TalosPrimes</strong></p>
    </div>
    <div class="footer">
      <p><strong>TalosPrimes</strong> - Plateforme SaaS de gestion d'entreprise</p>
      <p>Cet email a été envoyé automatiquement.</p>
    </div>
  </div>
</body>
</html>
`;

const textBody = `Bonjour ${data.prenom} ${data.nom},

Nous avons bien reçu votre demande d'inscription sur TalosPrimes.

Vos informations :
- Nom : ${data.nom}
- Prénom : ${data.prenom}
- Email : ${data.email}
- Téléphone : ${data.telephone}

Notre équipe va examiner votre demande et vous recontactera dans un délai de 24 à 48 heures.

Cordialement,
L'équipe TalosPrimes`;

return {
  json: {
    email: data.email,
    subject: "Demande d'inscription TalosPrimes - Confirmation",
    htmlBody: htmlBody,
    textBody: textBody
  }
};

// Formater les données pour SMS Twilio
const data = $input.all()[0].json;

return {
  json: {
    telephone: data.telephone || data.json?.telephone,
    message: `Bonjour ${data.prenom || data.json?.prenom} ${data.nom || data.json?.nom}, votre demande d'inscription TalosPrimes a été reçue. Vous serez recontacté par notre équipe dans les 24 à 48 heures. Merci de votre confiance !`
  }
};

