const payload = $input.body;

if (!payload.tenantId) {
  throw new Error('tenantId is required');
}

const page = parseInt(payload.page) || 1;
const limit = parseInt(payload.limit) || 10;
const offset = (page - 1) * limit;
const workflow = payload.workflow || null;
const typeEvenement = payload.typeEvenement || null;
const statutExecution = payload.statutExecution || null;

return {
  tenantId: payload.tenantId,
  page,
  limit,
  offset,
  workflow,
  typeEvenement,
  statutExecution
};

const logs = $('06. List Query').all();
const countResult = $('05. Count Query').first();
const total = countResult.total;
const totalPages = Math.ceil(total / $json.limit);

return {
  logs: logs,
  count: logs.length,
  total: total,
  page: $json.page,
  limit: $json.limit,
  totalPages: totalPages
};
