return [{ json: {
  callLogId: $json.id,
  success: true,
  message: 'Call log updated successfully'
} }];

var data = $input.first().json;
var setClauses = [];
var params = [];
var idx = 1;

if (data.leadId !== undefined) {
  setClauses.push('lead_id = $' + idx);
  params.push(data.leadId || null);
  idx++;
}

if (data.agentId !== undefined) {
  setClauses.push('agent_id = $' + idx);
  params.push(data.agentId || null);
  idx++;
}

if (data.endedAt !== undefined) {
  setClauses.push('ended_at = $' + idx);
  params.push(data.endedAt || null);
  idx++;
}

if (data.duration !== undefined) {
  setClauses.push('duration = $' + idx);
  params.push(parseFloat(data.duration) || 0);
  idx++;
}

if (data.status !== undefined) {
  setClauses.push('status = $' + idx);
  params.push(data.status);
  idx++;
}

if (data.transcript !== undefined) {
  setClauses.push('transcript = $' + idx);
  params.push(data.transcript);
  idx++;
}

if (data.summary !== undefined) {
  setClauses.push('summary = $' + idx);
  params.push(data.summary);
  idx++;
}

if (data.actionTaken !== undefined) {
  setClauses.push('action_taken = $' + idx);
  params.push(data.actionTaken || null);
  idx++;
}

if (data.urgency !== undefined) {
  setClauses.push('urgency = $' + idx);
  params.push(data.urgency);
  idx++;
}

if (data.sentiment !== undefined) {
  setClauses.push('sentiment = $' + idx);
  params.push(data.sentiment || null);
  idx++;
}

if (data.notes !== undefined) {
  setClauses.push('notes = $' + idx);
  params.push(data.notes);
  idx++;
}

if (data.recordingUrl !== undefined) {
  setClauses.push('recording_url = $' + idx);
  params.push(data.recordingUrl || null);
  idx++;
}

setClauses.push('updated_at = NOW()');

var whereParts = [];
whereParts.push('id = $' + idx);
params.push(data.id);
idx++;
whereParts.push('tenant_id = $' + idx);
params.push(data.tenantId);
idx++;

var query = 'UPDATE call_logs SET ' + setClauses.join(', ') + ' WHERE ' + whereParts.join(' AND ');

return [{ json: { ...data, query, params } }];
