const body = $input.body;
return {
  id: body.id,
  tenantId: body.tenantId,
  leadId: body.leadId,
  callLogId: body.callLogId,
  title: body.title,
  description: body.description,
  content: body.content,
  status: body.status,
  completedAt: body.completedAt
};

return {
  questionnaireId: $json.id,
  success: true,
  message: 'Questionnaire updated successfully'
};

const data = $input.first().json;
const setClauses = [];
const params = [];
let idx = 1;

if (data.leadId !== undefined) {
  setClauses.push('lead_id = $' + idx);
  params.push(data.leadId || null);
  idx++;
}

if (data.callLogId !== undefined) {
  setClauses.push('call_log_id = $' + idx);
  params.push(data.callLogId || null);
  idx++;
}

if (data.title !== undefined) {
  setClauses.push('title = $' + idx);
  params.push(data.title);
  idx++;
}

if (data.description !== undefined) {
  setClauses.push('description = $' + idx);
  params.push(data.description);
  idx++;
}

if (data.content !== undefined) {
  setClauses.push('content = $' + idx + '::jsonb');
  params.push(JSON.stringify(data.content));
  idx++;
}

if (data.status !== undefined) {
  setClauses.push('status = $' + idx);
  params.push(data.status);
  idx++;
}

if (data.completedAt !== undefined) {
  setClauses.push('completed_at = $' + idx);
  params.push(data.completedAt || null);
  idx++;
}

setClauses.push('updated_at = NOW()');

const whereParts = [];
whereParts.push('id = $' + idx);
params.push(data.id);
idx++;
whereParts.push('tenant_id = $' + idx);
params.push(data.tenantId);
idx++;

const query = 'UPDATE questionnaires SET ' + setClauses.join(', ') + ' WHERE ' + whereParts.join(' AND ');

return [{ json: { ...data, query, params } }];
