const raw = $input.first().json;
const _wb = (raw && raw.body && typeof raw.body === "object") ? raw.body : raw;
const body = (_wb && _wb.data && typeof _wb.data === "object") ? { ..._wb.data, tenantId: _wb.data.tenantId || _wb.tenantId } : _wb;
return {
  id: body.id,
  tenantId: body.tenantId,
  leadId: body.leadId,
  callLogId: body.callLogId,
  title: body.title,
  description: body.description,
  content: body.content,
  status: body.status,
  completedAt: body.completedAt
};

return {
  questionnaireId: $json.id,
  success: true,
  message: 'Questionnaire updated successfully'
};
