const raw = $input.first().json;
const _wb = (raw && raw.body && typeof raw.body === "object") ? raw.body : raw;
const body = (_wb && _wb.data && typeof _wb.data === "object") ? { ..._wb.data, tenantId: _wb.data.tenantId || _wb.tenantId } : _wb;
return {
  id: require('crypto').randomUUID(),
  tenantId: body.tenantId,
  leadId: body.leadId || null,
  phoneNumber: body.phoneNumber,
  message: body.message,
  status: body.status || 'sent',
  direction: body.direction || 'outbound',
  templateId: body.templateId || null,
  campaignId: body.campaignId || null,
  sentAt: body.sentAt || new Date().toISOString(),
  deliveredAt: body.deliveredAt || null,
  failureReason: body.failureReason || null
};

return {
  smsLogId: $json.id,
  phoneNumber: $json.phoneNumber,
  status: $json.status,
  sentAt: $json.sentAt,
  success: true,
  message: 'SMS log created successfully'
};
