// Parse Twilio webhook payload (form-urlencoded)
const data = $input.first().json;
const body = data.body || data;

const callSid = body.CallSid || '';
const from = body.From || '';
const to = body.To || '';
const callStatus = body.CallStatus || '';
const speechResult = body.SpeechResult || '';
const digits = body.Digits || '';
const isCallback = !!speechResult || !!digits;

return [{json: {
  callSid,
  from,
  to,
  callStatus,
  speechResult,
  digits,
  isCallback
}}];

// Premier appel : message d'accueil + Gather
const callSid = $('02. Parser Twilio').first().json.callSid;
const from = $('02. Parser Twilio').first().json.from;

const twiml = `<?xml version="1.0" encoding="UTF-8"?>
<Response>
  <Say voice="Polly.Lea-Neural" language="fr-FR">TalosPrimes, bonjour ! Comment puis-je vous aider ?</Say>
  <Gather input="speech" language="fr-FR" speechTimeout="auto" timeout="8" action="https://n8n.talosprimes.com/webhook/twilio-inbound-voice" method="POST">
    <Say voice="Polly.Lea-Neural" language="fr-FR">Je vous écoute.</Say>
  </Gather>
  <Say voice="Polly.Lea-Neural" language="fr-FR">Je n'ai pas entendu de réponse. Au revoir !</Say>
</Response>`;

return { twiml, callSid, from };

// Callback avec SpeechResult
const prev = $('02. Parser Twilio').first().json;
const speechResult = prev.speechResult;
const callSid = prev.callSid;
const from = prev.from;

if (!speechResult || speechResult.trim() === '') {
  return [{json: { callSid, from, speechResult: '', hasText: false }}];
}

return [{json: { callSid, from, speechResult: speechResult.trim(), hasText: true }}];

// Pas de texte détecté, on relance l'écoute
const twiml = `<?xml version="1.0" encoding="UTF-8"?>
<Response>
  <Say voice="Polly.Lea-Neural" language="fr-FR">Excusez-moi, je n'ai pas compris. Pouvez-vous répéter ?</Say>
  <Gather input="speech" language="fr-FR" speechTimeout="auto" timeout="8" action="https://n8n.talosprimes.com/webhook/twilio-inbound-voice" method="POST">
    <Say voice="Polly.Lea-Neural" language="fr-FR">Je vous écoute.</Say>
  </Gather>
  <Say voice="Polly.Lea-Neural" language="fr-FR">Au revoir.</Say>
</Response>`;

return { twiml };

// Détecter l'admin avec la phrase secrète + charger le contexte de conversation
const speechResult = $('06. Extract Speech').first().json.speechResult;
const callSid = $('06. Extract Speech').first().json.callSid;
const from = $('06. Extract Speech').first().json.from;

// Détection admin : phrase secrète "c'est giizmo" (insensible à la casse)
const isAdminPhrase = speechResult.toLowerCase().includes('giizmo') ||
                     speechResult.toLowerCase().includes('gizmo') ||
                     speechResult.toLowerCase().includes('gismo') ||
                     speechResult.toLowerCase().includes("c'est giizmo");

return {
  callSid,
  from,
  speechResult,
  isAdminPhrase,
  mode: isAdminPhrase ? 'admin' : 'standard'
};

// Construire le prompt système pour OpenAI (optimisé vocal)
const prev = $('10. Detect Admin').first().json;
const session = $('11. Get Session').first().json;
const isAdmin = prev.isAdminPhrase;
const speechResult = prev.speechResult;
const callSid = prev.callSid;
const from = prev.from;
const clientName = session.nom || '';
const clientInfo = clientName ? `L'appelant s'appelle ${clientName}.` : '';

const standardPrompt = `Tu es l'assistante vocale de TalosPrimes, une plateforme de gestion immobilière tout-en-un pour les professionnels.
CONTEXTE: TalosPrimes aide les agences immobilières, administrateurs de biens et promoteurs à gérer leurs activités : gestion locative, syndic de copropriété, transactions, comptabilité, états des lieux numériques, signature électronique, et CRM intégré.
${clientInfo}
RÈGLES VOCALES:
- Réponds en maximum deux phrases courtes
- Parle naturellement, comme une vraie personne au téléphone
- Pas de listes, pas de formatage, pas d'émojis
- Si tu ne sais pas, dis simplement que tu vas transmettre au service concerné
- Tutoie pas, vouvoie toujours
- Sois chaleureuse et professionnelle`;

const adminPrompt = `Tu es l'assistante IA de Giizmo, administrateur de TalosPrimes. Tu as accès complet au système.
${clientInfo}
RÈGLES:
- Réponds en maximum trois phrases
- Sois directe et efficace
- Tu peux donner des infos techniques détaillées`;

return { systemPrompt: isAdmin ? adminPrompt : standardPrompt, speechResult, callSid, from, isAdmin };

// Extraire la réponse OpenAI (format HTTP Request) et construire le TwiML
const openaiResponse = $input.first().json;
const prev = $('12. Build Prompt').first().json;

let aiReply = openaiResponse.choices?.[0]?.message?.content || 'Excusez-moi, je n\'ai pas pu traiter votre demande. Puis-je vous aider autrement ?';

// Nettoyer la réponse pour le TTS
aiReply = aiReply
  .replace(/\*\*/g, '')
  .replace(/\*/g, '')
  .replace(/#{1,6}\s/g, '')
  .replace(/[\[\]]/g, '')
  .replace(/\n/g, ' ')
  .replace(/&/g, '&amp;')
  .replace(/</g, '&lt;')
  .replace(/>/g, '&gt;')
  .replace(/"/g, '&quot;')
  .trim();

// Détecter si l'utilisateur dit au revoir
const speech = prev.speechResult.toLowerCase();
const isGoodbye = speech.includes('au revoir') || speech.includes('merci au revoir') || 
                  speech.includes('bonne journée') || speech.includes('à bientôt') ||
                  speech.includes("c'est tout") || speech.includes('terminé');

let twiml = '';
if (isGoodbye) {
  twiml = `<?xml version="1.0" encoding="UTF-8"?>
<Response>
  <Say voice="Polly.Lea-Neural" language="fr-FR">${aiReply} Au revoir et bonne journée !</Say>
  <Hangup/>
</Response>`;
} else {
  twiml = `<?xml version="1.0" encoding="UTF-8"?>
<Response>
  <Say voice="Polly.Lea-Neural" language="fr-FR">${aiReply}</Say>
  <Gather input="speech" language="fr-FR" speechTimeout="auto" timeout="8" action="https://n8n.talosprimes.com/webhook/twilio-inbound-voice" method="POST">
    <Say voice="Polly.Lea-Neural" language="fr-FR">Avez-vous une autre question ?</Say>
  </Gather>
  <Say voice="Polly.Lea-Neural" language="fr-FR">Merci d'avoir appelé TalosPrimes. Bonne journée !</Say>
</Response>`;
}

return {
  twiml,
  callSid: prev.callSid,
  from: prev.from,
  speechResult: prev.speechResult,
  aiReply: aiReply.substring(0, 500),
  isAdmin: prev.isAdmin,
  isGoodbye
};
