// Parse Twilio webhook payload (form-urlencoded) avec validation anti-injection
var data = $input.first().json;
var body = data.body || data;

// Regex de validation pour les champs Twilio
var CALLSID_RE = /^CA[0-9a-f]{32}$/i;
var PHONE_RE = /^\+?[0-9]{6,15}$/;

// Nettoyer : ne garder que les caractères attendus
function sanitizePhone(p) { return String(p || '').replace(/[^+0-9]/g, '').substring(0, 16); }
function sanitizeCallSid(s) { return String(s || '').replace(/[^a-zA-Z0-9]/g, '').substring(0, 34); }
function sanitizeText(t) { return String(t || '').replace(/'/g, "''").substring(0, 2000); }

var callSid = sanitizeCallSid(body.CallSid);
var from = sanitizePhone(body.From);
var to = sanitizePhone(body.To);
var callStatus = String(body.CallStatus || '').replace(/[^a-z-]/gi, '').substring(0, 30);
var speechResult = sanitizeText(body.SpeechResult);
var digits = String(body.Digits || '').replace(/[^0-9*#]/g, '').substring(0, 20);
var isCallback = !!speechResult || !!digits;

return [{json: {
  callSid,
  from,
  to,
  callStatus,
  speechResult,
  digits,
  isCallback
}}];

// Premier appel : message d\u0027accueil + Gather
var callSid = $('02. Parser Twilio').first().json.callSid;
var from = $('02. Parser Twilio').first().json.from;

var twiml = `<?xml version="1.0" encoding="UTF-8"?>
<Response>
  <Say voice="Polly.Lea-Neural" language="fr-FR">TalosPrimes, bonjour ! Comment puis-je vous aider ?</Say>
  <Gather input="speech" language="fr-FR" speechTimeout="3" timeout="15" action="https://n8n.talosprimes.com/webhook/twilio-inbound-voice" method="POST"/>
  <Say voice="Polly.Lea-Neural" language="fr-FR">Je n\u0027ai pas entendu votre réponse de réponse. Au revoir !</Say>
</Response>`;

return [{ json: { twiml, callSid, from } }];

// Callback avec SpeechResult
var prev = $('02. Parser Twilio').first().json;
var speechResult = prev.speechResult;
var callSid = prev.callSid;
var from = prev.from;

if (!speechResult || speechResult.trim() === '') {
  return [{json: { callSid, from, speechResult: '', hasText: false }}];
}

return [{json: { callSid, from, speechResult: speechResult.trim(), hasText: true }}];

// Pas de texte détecté, on relance l ecoute
var twiml = `<?xml version="1.0" encoding="UTF-8"?>
<Response>
  <Say voice="Polly.Lea-Neural" language="fr-FR">Excusez-moi, Je n\u0027ai pas bien compris. Pouvez-vous répéter ?</Say>
  <Gather input="speech" language="fr-FR" speechTimeout="3" timeout="15" action="https://n8n.talosprimes.com/webhook/twilio-inbound-voice" method="POST"/>
  <Say voice="Polly.Lea-Neural" language="fr-FR">Au revoir.</Say>
</Response>`;

return [{ json: { twiml } }];

// Mode admin désactivé — tout en mode standard
var speechResult = $('06. Extract Speech').first().json.speechResult;
var callSid = $('06. Extract Speech').first().json.callSid;
var from = $('06. Extract Speech').first().json.from;

return [{ json: {
  callSid,
  from,
  speechResult,
  isAdminPhrase: false,
  isAdminByPhone: false,
  mode: 'standard'
} }];

// Construire le prompt avec FAQ + historique + tools — ZERO FALLBACK
var prev = $('10. Detect Admin').first().json;
var session = $('11. Get Session').first().json;
if (!session || !session.tenant_id) throw new Error('Get Session: tenant_id manquant. Verifier que le numero Twilio est bien configure.');
var speechResult = prev.speechResult;
var callSid = prev.callSid;
var from = prev.from;
var clientName = session.nom || '';
var clientPrenom = session.prenom || '';
var clientEmail = session.email || '';
var clientId = session.id || '';
var tenantId = session.tenant_id;

// Info client
var clientInfo = '';
if (clientName) {
  clientInfo = 'Client identifie: ' + clientPrenom + ' ' + clientName;
  if (clientEmail) clientInfo += ' (' + clientEmail + ')';
  clientInfo += '. ID: ' + clientId;
} else {
  clientInfo = 'Appelant non identifie dans notre base.';
}

// Historique de conversation — pas de try/catch, pas de fallback
var conversationHistory = '';
var historyItems = $('20. Load History').all();
var exchanges = [];
for (var i = 0; i < historyItems.length; i++) {
  var item = historyItems[i];
  var notes = '';
  if (item && item.json && item.json.notes) {
    notes = String(item.json.notes);
  } else if (item && item.notes) {
    notes = String(item.notes);
  }
  if (!notes) continue;
  var qMatch = notes.match(/Q: (.+?) - R: /);
  var rMatch = notes.match(/R: (.+)$/);
  if (qMatch && rMatch) {
    exchanges.push('Appelant: ' + qMatch[1] + '\nLea: ' + rMatch[1]);
  }
}
if (exchanges.length > 0) {
  conversationHistory = '\nHISTORIQUE DE LA CONVERSATION:\n' + exchanges.join('\n') + '\n';
}

// FAQ depuis la base de connaissances — pas de try/catch, pas de fallback
var faqContext = '';
var knowledgeItems = $('11b. Load Knowledge').all();
if (!knowledgeItems || knowledgeItems.length === 0) throw new Error('Load Knowledge: aucune donnee retournee. Verifier la requete SQL et la table agent_knowledge_entries pour tenant ' + tenantId);
var byCategorie = {};
var hasRealData = false;
for (var i = 0; i < knowledgeItems.length; i++) {
  var row = knowledgeItems[i];
  var data = (row && row.json) ? row.json : row;
  if (!data || !data.contenu) continue;
  hasRealData = true;
  var cat = data.categorie || 'autre';
  if (!byCategorie[cat]) byCategorie[cat] = [];
  byCategorie[cat].push(data);
}
if (!hasRealData) throw new Error('Load Knowledge: les items existent mais aucun ne contient de champ contenu. Verifier la structure des donnees pour tenant ' + tenantId);
var catLabels = {
  'info_entreprise': 'INFORMATIONS ENTREPRISE',
  'services': 'MODULES ET SERVICES',
  'tarifs': 'TARIFICATION',
  'faq': 'FAQ',
  'politiques': 'POLITIQUES',
  'actions': 'ACTIONS POSSIBLES',
  'autre': 'AUTRE'
};
var sections = [];
var catOrder = ['info_entreprise', 'services', 'tarifs', 'faq', 'politiques', 'actions', 'autre'];
for (var j = 0; j < catOrder.length; j++) {
  var c = catOrder[j];
  if (byCategorie[c] && byCategorie[c].length > 0) {
    var lines = [];
    for (var k = 0; k < byCategorie[c].length; k++) {
      var entry = byCategorie[c][k];
      lines.push((entry.titre || '') + ': ' + (entry.contenu || ''));
    }
    sections.push((catLabels[c] || c) + ':\n' + lines.join('\n'));
  }
}
faqContext = sections.join('\n\n');
if (!faqContext) throw new Error('Load Knowledge: faqContext vide apres construction. Donnees presentes mais aucune section generee pour tenant ' + tenantId);

// Construire le prompt systeme
var systemPrompt = 'Tu es Lea, assistante vocale de TalosPrimes.\n'
  + clientInfo + '\n'
  + conversationHistory + '\n'
  + faqContext + '\n\n'
  + 'TU AS DES OUTILS pour creer des leads et rechercher des clients. Utilise-les quand cela est pertinent.\n\n'
  + 'COMPORTEMENT:\n'
  + '1. REPONDS aux questions sur la plateforme avec des infos PRECISES\n'
  + '2. Si prospect interesse: utilise create_lead pour enregistrer ses coordonnees\n'
  + '3. Si probleme TECHNIQUE COMPLEXE: dis que le service technique le recontactera sous 24h\n'
  + '4. Si CLIENT avec souci simple: GUIDE-LE etape par etape\n'
  + '5. JAMAIS dire contactez un administrateur - TU ES le point de contact\n'
  + '6. NE REPETE JAMAIS la meme question\n\n'
  + 'REGLES VOCALES:\n'
  + '- Maximum 3 phrases courtes et naturelles\n'
  + '- Parle comme une vraie personne, chaleureuse et pro\n'
  + '- Vouvoie toujours\n'
  + '- Pas de listes, pas de formatage';

// Tools disponibles
var tools = [
  {
    type: "function",
    function: {
      name: "create_lead",
      description: "Creer un nouveau prospect dans le CRM quand un appelant donne ses coordonnees.",
      parameters: {
        type: "object",
        properties: {
          nom: { type: "string", description: "Nom de famille" },
          prenom: { type: "string", description: "Prenom" },
          email: { type: "string", description: "Adresse email" },
          telephone: { type: "string", description: "Numero de telephone" },
          notes: { type: "string", description: "Notes sur la demande" }
        },
        required: ["nom", "telephone"]
      }
    }
  },
  {
    type: "function",
    function: {
      name: "search_client",
      description: "Rechercher un client par nom ou telephone.",
      parameters: {
        type: "object",
        properties: {
          query: { type: "string", description: "Nom, prenom ou telephone a rechercher" }
        },
        required: ["query"]
      }
    }
  }
];

return [{ json: {
  systemPrompt: systemPrompt,
  tools: JSON.stringify(tools),
  speechResult: speechResult,
  callSid: callSid,
  from: from,
  isAdmin: false,
  clientName: clientName,
  clientEmail: clientEmail,
  clientId: clientId,
  tenantId: tenantId
} }];

// Construire TwiML à partir de la réponse IA (directe ou après tool call)
var input = $input.first().json;
if (!input) throw new Error('Build TwiML: aucune donnee en entree. Le node precedent n a rien retourne.');

var aiReply = input.aiReply;
if (!aiReply) throw new Error('Build TwiML: aiReply manquant. La reponse OpenAI n a pas ete transmise.');

// Nettoyer pour TTS
aiReply = aiReply
  .replace(/\*\*/g, '')
  .replace(/\*/g, '')
  .replace(/#{1,6}\s/g, '')
  .replace(/[\[\]]/g, '')
  .replace(/\\n/g, ' ')
  .replace(/&/g, '&amp;')
  .replace(/</g, '&lt;')
  .replace(/>/g, '&gt;')
  .replace(/"/g, '&quot;')
  .trim();

// Détecter intentions
var speech = (input.speechResult || '').toLowerCase();
var isGoodbye = speech.includes('au revoir') || speech.includes('merci au revoir') ||
                  speech.includes('bonne journée') || speech.includes('à bientôt') ||
                  speech.includes("c est tout") || speech.includes('terminé');

var isProspect = speech.includes('intéress') || speech.includes('tarif') || speech.includes('prix') ||
                   speech.includes('abonnement') || speech.includes('essai') || speech.includes('demo') ||
                   speech.includes('souscrire') || speech.includes('inscription') || speech.includes('devis') ||
                   (input.toolUsed === 'create_lead');

var needsEscalation = speech.includes('bug') || speech.includes('erreur') || speech.includes('bloqué') ||
                        speech.includes('marche pas') || speech.includes('fonctionne pas') ||
                        speech.includes('données perdues') || speech.includes('impossible') ||
                        speech.includes('urgent') || speech.includes('grave');

var twiml = '';
if (isGoodbye) {
  twiml = `<?xml version="1.0" encoding="UTF-8"?>
<Response>
  <Say voice="Polly.Lea-Neural" language="fr-FR">${aiReply}</Say>
  <Hangup/>
</Response>`;
} else {
  twiml = `<?xml version="1.0" encoding="UTF-8"?>
<Response>
  <Say voice="Polly.Lea-Neural" language="fr-FR">${aiReply}</Say>
  <Gather input="speech" language="fr-FR" speechTimeout="3" timeout="15" action="https://n8n.talosprimes.com/webhook/twilio-inbound-voice" method="POST"/>
  <Say voice="Polly.Lea-Neural" language="fr-FR">Merci d\u0027avoir appelé TalosPrimes. Bonne journée !</Say>
</Response>`;
}

return [{ json: {
  twiml,
  callSid: input.callSid,
  from: input.from,
  speechResult: input.speechResult,
  aiReply: aiReply.substring(0, 500),
  isAdmin: input.isAdmin,
  isGoodbye,
  isProspect,
  needsEscalation,
  clientName: input.clientName || '',
  toolUsed: input.toolUsed || ''
} }];

// Parse la réponse OpenAI avec requêtes SQL paramétrisées
var openaiResponse = $input.first().json;
var prev = $('12. Build Prompt').first().json;
if (!prev || !prev.systemPrompt) throw new Error('Build Prompt: donnees manquantes. Le node 12 n a pas retourne de systemPrompt.');
var message = openaiResponse.choices?.[0]?.message;
var finishReason = openaiResponse.choices?.[0]?.finish_reason;

// === FONCTIONS DE VALIDATION ===
var UUID_RE = /^[0-9a-f]{8}-[0-9a-f]{4}-4[0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
var PHONE_RE = /^\+?[0-9]{6,15}$/;
var EMAIL_RE = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

function sanitizePhone(p) { return String(p || '').replace(/[^+0-9]/g, '').substring(0, 16); }
function validateNum(n, min, max) { var v = parseFloat(n); return (Number.isFinite(v) && v >= min && v <= max) ? v : 0; }
function safe(s, maxLen) { return String(s || '').substring(0, maxLen || 500); }

if (finishReason === 'tool_calls' && message?.tool_calls?.length > 0) {
  var toolCall = message.tool_calls[0];
  var toolName = toolCall.function.name;
  var toolArgs = JSON.parse(toolCall.function.arguments || '{}');
  var toolCallId = toolCall.id;
  var tenantId = prev.tenantId || '';

  var ALLOWED_TOOLS = ['create_lead', 'search_client', 'create_client', 'create_invoice', 'create_bon_commande', 'list_recent_leads', 'update_lead', 'delete_lead', 'update_client', 'delete_client', 'list_clients', 'list_invoices', 'update_invoice_status', 'delete_invoice'];
  if (!ALLOWED_TOOLS.includes(toolName)) throw new Error('Tool non autorisé: ' + toolName);

  var sqlQuery = '';
  var sqlParams = [];

  if (toolName === 'create_lead') {
    var nom = safe(toolArgs.nom, 100);
    var prenom = safe(toolArgs.prenom, 100);
    var email = toolArgs.email && EMAIL_RE.test(toolArgs.email) ? safe(toolArgs.email, 255) : '';
    var tel = sanitizePhone(toolArgs.telephone || prev.from);
    var notes = safe(toolArgs.notes, 500);
    if (!tel) throw new Error('Téléphone requis pour create_lead');
    sqlQuery = `INSERT INTO leads (tenant_id, nom, prenom, email, telephone, source, statut, notes, created_at, updated_at)
      SELECT COALESCE(NULLIF($1, '')::uuid, (SELECT id FROM tenants LIMIT 1)), $2, $3, $4, $5, 'telephone_ia', 'nouveau', $6, NOW(), NOW()
      WHERE NOT EXISTS (SELECT 1 FROM leads WHERE telephone LIKE '%' || RIGHT($5, 9) || '%' AND statut != 'abandonne')
      RETURNING id, nom, prenom, telephone`;
    sqlParams = [tenantId, nom, prenom, email, tel, notes];
  } else if (toolName === 'search_client') {
    var q = safe(toolArgs.query, 100);
    if (!q) throw new Error('Query requis pour search_client');
    sqlQuery = `SELECT id, nom, prenom, email, telephone, raison_sociale, type, statut FROM client_finals WHERE nom ILIKE '%' || $1 || '%' OR prenom ILIKE '%' || $1 || '%' OR telephone LIKE '%' || $1 || '%' OR email ILIKE '%' || $1 || '%' LIMIT 5`;
    sqlParams = [q];
  } else if (toolName === 'create_client') {
    var email = toolArgs.email && EMAIL_RE.test(toolArgs.email) ? safe(toolArgs.email, 255) : '';
    if (!email) throw new Error('Email valide requis pour create_client');
    var type = ['b2b', 'b2c'].includes(toolArgs.type) ? toolArgs.type : 'b2c';
    sqlQuery = `INSERT INTO client_finals (tenant_id, type, nom, prenom, email, telephone, raison_sociale, statut, created_at, updated_at)
      VALUES ((SELECT id FROM tenants LIMIT 1), $1, $2, $3, $4, $5, $6, 'actif', NOW(), NOW())
      RETURNING id, nom, prenom, email`;
    sqlParams = [type, safe(toolArgs.nom, 100), safe(toolArgs.prenom, 100), email, sanitizePhone(toolArgs.telephone), safe(toolArgs.raison_sociale, 255)];
  } else if (toolName === 'create_invoice') {
    var ht = validateNum(toolArgs.montant_ht, 0, 999999);
    var tva = validateNum(toolArgs.tva_taux || 20, 0, 100);
    var ttc = ht * (1 + tva / 100);
    var clientQ = safe(toolArgs.client_name_or_phone, 100);
    if (!clientQ || ht <= 0) throw new Error('Client et montant requis pour create_invoice');
    var numFacture = 'FA-' + Date.now();
    sqlQuery = `INSERT INTO invoices (tenant_id, type, client_final_id, numero_facture, date_facture, date_echeance, montant_ht, montant_ttc, tva_taux, description, statut, created_at, updated_at)
      SELECT (SELECT id FROM tenants LIMIT 1), 'facture_client_final',
        (SELECT id FROM client_finals WHERE nom ILIKE '%' || $1 || '%' OR telephone LIKE '%' || $1 || '%' LIMIT 1),
        $2, NOW(), NOW() + INTERVAL '30 days', $3, $4, $5, $6, 'brouillon', NOW(), NOW()
      RETURNING id, numero_facture, montant_ht, montant_ttc, statut`;
    sqlParams = [clientQ, numFacture, ht, parseFloat(ttc.toFixed(2)), tva, safe(toolArgs.description, 500)];
  } else if (toolName === 'create_bon_commande') {
    var ht = validateNum(toolArgs.montant_ht, 0, 999999);
    var tva = validateNum(toolArgs.tva_taux || 20, 0, 100);
    var ttc = ht * (1 + tva / 100);
    var clientQ = safe(toolArgs.client_name_or_phone, 100);
    if (!clientQ || ht <= 0) throw new Error('Client et montant requis pour create_bon_commande');
    var numBdc = 'BDC-' + Date.now();
    sqlQuery = `INSERT INTO bons_commande (tenant_id, client_final_id, numero_bdc, date_bdc, montant_ht, montant_ttc, tva_taux, description, statut, created_at, updated_at)
      SELECT (SELECT id FROM tenants LIMIT 1),
        (SELECT id FROM client_finals WHERE nom ILIKE '%' || $1 || '%' OR telephone LIKE '%' || $1 || '%' LIMIT 1),
        $2, NOW(), $3, $4, $5, $6, 'brouillon', NOW(), NOW()
      RETURNING id, numero_bdc, montant_ht, montant_ttc, statut`;
    sqlParams = [clientQ, numBdc, ht, parseFloat(ttc.toFixed(2)), tva, safe(toolArgs.description, 500)];
  } else if (toolName === 'list_recent_leads') {
    var limit = Math.min(Math.max(parseInt(toolArgs.limit) || 5, 1), 20);
    var statut = ['nouveau', 'contacte', 'converti', 'abandonne'].includes(toolArgs.statut) ? toolArgs.statut : null;
    if (statut) {
      sqlQuery = `SELECT id, nom, prenom, telephone, email, statut, source, created_at FROM leads WHERE statut = $1 ORDER BY created_at DESC LIMIT $2`;
      sqlParams = [statut, limit];
    } else {
      sqlQuery = `SELECT id, nom, prenom, telephone, email, statut, source, created_at FROM leads ORDER BY created_at DESC LIMIT $1`;
      sqlParams = [limit];
    }
  } else if (toolName === 'update_lead') {
    var q = safe(toolArgs.lead_id_or_phone, 100);
    if (!q) throw new Error('ID ou téléphone requis pour update_lead');
    var sets = [];
    var params = [q];
    var idx = 2;
    if (toolArgs.statut && ['nouveau', 'contacte', 'converti', 'abandonne'].includes(toolArgs.statut)) { sets.push(`statut = $${idx}`); params.push(toolArgs.statut); idx++; }
    if (toolArgs.notes) { sets.push(`notes = $${idx}`); params.push(safe(toolArgs.notes, 500)); idx++; }
    if (toolArgs.nom) { sets.push(`nom = $${idx}`); params.push(safe(toolArgs.nom, 100)); idx++; }
    if (toolArgs.email && EMAIL_RE.test(toolArgs.email)) { sets.push(`email = $${idx}`); params.push(safe(toolArgs.email, 255)); idx++; }
    if (sets.length === 0) throw new Error('Aucune modification spécifiée');
    sets.push('updated_at = NOW()');
    sqlQuery = `UPDATE leads SET ${sets.join(', ')} WHERE (id::text = $1 OR telephone LIKE '%' || RIGHT($1, 9) || '%') RETURNING id, nom, prenom, telephone, statut`;
    sqlParams = params;
  } else if (toolName === 'delete_lead') {
    var q = safe(toolArgs.lead_id_or_phone, 100);
    if (!q) throw new Error('ID ou téléphone requis pour delete_lead');
    sqlQuery = `DELETE FROM leads WHERE (id::text = $1 OR telephone LIKE '%' || RIGHT($1, 9) || '%') RETURNING id, nom, prenom, telephone`;
    sqlParams = [q];
  } else if (toolName === 'update_client') {
    var q = safe(toolArgs.client_id_or_name, 100);
    if (!q) throw new Error('ID ou nom requis pour update_client');
    var sets = [];
    var params = [q];
    var idx = 2;
    if (toolArgs.nom) { sets.push(`nom = $${idx}`); params.push(safe(toolArgs.nom, 100)); idx++; }
    if (toolArgs.email && EMAIL_RE.test(toolArgs.email)) { sets.push(`email = $${idx}`); params.push(safe(toolArgs.email, 255)); idx++; }
    if (toolArgs.telephone) { sets.push(`telephone = $${idx}`); params.push(sanitizePhone(toolArgs.telephone)); idx++; }
    if (toolArgs.statut && ['actif', 'inactif', 'suspendu'].includes(toolArgs.statut)) { sets.push(`statut = $${idx}`); params.push(toolArgs.statut); idx++; }
    if (sets.length === 0) throw new Error('Aucune modification spécifiée');
    sets.push('updated_at = NOW()');
    sqlQuery = `UPDATE client_finals SET ${sets.join(', ')} WHERE (id::text = $1 OR nom ILIKE '%' || $1 || '%' OR telephone LIKE '%' || RIGHT($1, 9) || '%') RETURNING id, nom, prenom, email, telephone, statut`;
    sqlParams = params;
  } else if (toolName === 'delete_client') {
    var q = safe(toolArgs.client_id_or_name, 100);
    if (!q) throw new Error('ID ou nom requis pour delete_client');
    sqlQuery = `DELETE FROM client_finals WHERE (id::text = $1 OR nom ILIKE '%' || $1 || '%') RETURNING id, nom, prenom, email`;
    sqlParams = [q];
  } else if (toolName === 'list_clients') {
    var limit = Math.min(Math.max(parseInt(toolArgs.limit) || 10, 1), 50);
    var statut = ['actif', 'inactif', 'suspendu'].includes(toolArgs.statut) ? toolArgs.statut : null;
    var type = ['b2b', 'b2c'].includes(toolArgs.type) ? toolArgs.type : null;
    if (statut && type) {
      sqlQuery = `SELECT id, nom, prenom, email, telephone, type, statut, raison_sociale, created_at FROM client_finals WHERE statut = $1 AND type = $2 ORDER BY created_at DESC LIMIT $3`;
      sqlParams = [statut, type, limit];
    } else if (statut) {
      sqlQuery = `SELECT id, nom, prenom, email, telephone, type, statut, raison_sociale, created_at FROM client_finals WHERE statut = $1 ORDER BY created_at DESC LIMIT $2`;
      sqlParams = [statut, limit];
    } else if (type) {
      sqlQuery = `SELECT id, nom, prenom, email, telephone, type, statut, raison_sociale, created_at FROM client_finals WHERE type = $1 ORDER BY created_at DESC LIMIT $2`;
      sqlParams = [type, limit];
    } else {
      sqlQuery = `SELECT id, nom, prenom, email, telephone, type, statut, raison_sociale, created_at FROM client_finals ORDER BY created_at DESC LIMIT $1`;
      sqlParams = [limit];
    }
  } else if (toolName === 'list_invoices') {
    var limit = Math.min(Math.max(parseInt(toolArgs.limit) || 10, 1), 50);
    var statut = ['brouillon', 'envoyee', 'payee', 'en_retard', 'annulee'].includes(toolArgs.statut) ? toolArgs.statut : null;
    if (statut) {
      sqlQuery = `SELECT i.id, i.numero_facture, i.statut, i.montant_ht, i.montant_ttc, i.date_facture, i.date_echeance, cf.nom as client_nom, cf.prenom as client_prenom FROM invoices i LEFT JOIN client_finals cf ON i.client_final_id = cf.id WHERE i.statut = $1 ORDER BY i.created_at DESC LIMIT $2`;
      sqlParams = [statut, limit];
    } else {
      sqlQuery = `SELECT i.id, i.numero_facture, i.statut, i.montant_ht, i.montant_ttc, i.date_facture, i.date_echeance, cf.nom as client_nom, cf.prenom as client_prenom FROM invoices i LEFT JOIN client_finals cf ON i.client_final_id = cf.id ORDER BY i.created_at DESC LIMIT $1`;
      sqlParams = [limit];
    }
  } else if (toolName === 'update_invoice_status') {
    var q = safe(toolArgs.invoice_id_or_number, 100);
    var statut = toolArgs.statut;
    if (!q || !['brouillon', 'envoyee', 'payee', 'en_retard', 'annulee'].includes(statut)) throw new Error('ID/numéro et statut valide requis');
    sqlQuery = `UPDATE invoices SET statut = $1, updated_at = NOW() WHERE (id::text = $2 OR numero_facture = $2) RETURNING id, numero_facture, statut, montant_ttc`;
    sqlParams = [statut, q];
  } else if (toolName === 'delete_invoice') {
    var q = safe(toolArgs.invoice_id_or_number, 100);
    if (!q) throw new Error('ID ou numéro requis pour delete_invoice');
    sqlQuery = `DELETE FROM invoices WHERE (id::text = $1 OR numero_facture = $1) AND statut = 'brouillon' RETURNING id, numero_facture, montant_ttc`;
    sqlParams = [q];
  }

  return [{ json: {
    hasToolCall: true,
    toolCallId,
    toolName,
    toolArgs: JSON.stringify(toolArgs),
    sqlQuery,
    sqlParams,
    messages: JSON.stringify([
      {role: 'system', content: prev.systemPrompt},
      {role: 'user', content: prev.speechResult},
      message
    ]),
    tools: prev.tools,
    callSid: prev.callSid,
    from: prev.from,
    speechResult: prev.speechResult,
    isAdmin: prev.isAdmin,
    clientName: prev.clientName || '',
    clientEmail: prev.clientEmail || ''
  } }];
} else {
  var aiReply = message?.content;
  if (!aiReply) throw new Error('OpenAI Chat: pas de contenu dans la reponse directe. choices[0].message.content est vide.');

  return [{ json: {
    hasToolCall: false,
    aiReply,
    callSid: prev.callSid,
    from: prev.from,
    speechResult: prev.speechResult,
    isAdmin: prev.isAdmin,
    clientName: prev.clientName || '',
    clientEmail: prev.clientEmail || ''
  } }];
}

