const body = $input.body;
return {
  tenantId: body.tenantId,
  clientFinalId: body.clientFinalId,
  invoiceId: body.invoiceId || null,
  montantHt: body.montantHt,
  tvaTaux: body.tvaTaux || 0,
  dateAvoir: body.dateAvoir || new Date().toISOString().split('T')[0],
  motif: body.motif || '',
  description: body.description || '',
  lines: body.lines || []
};

const nextNum = $('Postgres Get Next Number').first().json.next_num;
const year = new Date().getFullYear();
const paddedNum = String(nextNum).padStart(6, '0');
const numeroAvoir = `AVO-${year}-${paddedNum}`;

return {
  ...($input.all()[0].json),
  numeroAvoir: numeroAvoir,
  avoirId: require('crypto').randomUUID()
};

const lines = $json.lines || [];
const avoirId = $json.avoirId;

if (lines.length === 0) {
  return { lineQueries: [], linesCount: 0 };
}

const lineQueries = lines.map((line, idx) => ({
  avoirId: avoirId,
  ordre: idx + 1,
  codeArticle: line.codeArticle || line.code_article || '',
  designation: line.designation || line.description || '',
  quantite: line.quantite || 1,
  prixUnitaireHt: parseFloat(line.prixUnitaireHt || line.prixUnitaire || line.prix_unitaire_ht || 0),
  totalHt: parseFloat(line.totalHt || line.montant || line.total_ht || 0)
}));

return { lineQueries, linesCount: lineQueries.length };
