const body = $input.body;
return {
  tenantId: body.tenantId,
  page: body.page || 1,
  limit: body.limit || 20,
  statut: body.statut || null,
  clientFinalId: body.clientFinalId || null,
  offset: ((body.page || 1) - 1) * (body.limit || 20)
};

const rawAvoirs = $('Postgres List').all().map(item => item.json);
const countResult = $('Postgres Count').first().json;
const total = parseInt(countResult.total) || 0;
const totalPages = Math.ceil(total / $json.limit) || 1;

// Nest client data into clientFinal object
const avoirs = rawAvoirs.map(a => ({
  ...a,
  clientFinal: a.client_final_id ? {
    id: a.client_final_id,
    nom: a.client_nom || null,
    prenom: a.client_prenom || null,
    email: a.client_email || null,
    telephone: a.client_telephone || null,
    raisonSociale: a.client_raison_sociale || null,
  } : null,
}));

return [{json: {
  avoirs,
  count: avoirs.length,
  total,
  page: $json.page,
  limit: $json.limit,
  totalPages,
}}];
