var body = $input.first().json.body || $input.first().json;

if (!body.tenantId || !body.bdcId) {
  throw new Error('tenantId and bdcId are required');
}

return [{ json: { tenantId: body.tenantId, bdcId: body.bdcId } }];

var bdc = $('05. Get BdC').first().json;
var nextNum = $('06. Get Next Invoice Number').first().json.next_num || 1;

if (!bdc || !bdc.id) {
  throw new Error('Bon de commande not found or not in valid status');
}

function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }
function num(v) { var n = parseFloat(v); return isNaN(n) ? '0' : String(n); }

var year = new Date().getFullYear();
var numeroFacture = 'INV-' + year + '-' + String(nextNum).padStart(6, '0');
var now = new Date().toISOString();
var echeance = new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString();

var insertQuery = "INSERT INTO invoices (id, type, tenant_id, client_final_id, numero_facture, date_facture, date_echeance, montant_ht, montant_ttc, tva_taux, description, mode_paiement, statut, created_at, updated_at) VALUES (gen_random_uuid(), 'facture_client_final', " + esc(bdc.tenant_id) + "::uuid, " + (bdc.client_final_id ? esc(bdc.client_final_id) + "::uuid" : "NULL") + ", " + esc(numeroFacture) + ", " + esc(now) + "::timestamptz, " + esc(echeance) + "::timestamptz, " + num(bdc.montant_ht) + ", " + num(bdc.montant_ttc) + ", " + num(bdc.tva_taux || 20) + ", " + esc(bdc.description ? 'BDC ' + bdc.numero_bdc + ' - ' + bdc.description : 'Depuis BDC ' + bdc.numero_bdc) + ", " + esc(bdc.mode_paiement) + ", 'brouillon', NOW(), NOW()) RETURNING id, tenant_id, client_final_id, type, numero_facture, date_facture, date_echeance, montant_ht, montant_ttc, tva_taux, description, mode_paiement, statut, created_at, updated_at";

var updateQuery = "UPDATE bons_commande SET statut = 'facture', updated_at = NOW() WHERE id = " + esc(bdc.id) + " RETURNING id, numero_bdc, statut";

return [{ json: {
  bdcId: bdc.id,
  tenantId: bdc.tenant_id,
  clientFinalId: bdc.client_final_id,
  numeroBdc: bdc.numero_bdc,
  numeroFacture: numeroFacture,
  dateFacture: now,
  dateEcheance: echeance,
  montantHt: bdc.montant_ht,
  montantTtc: bdc.montant_ttc,
  tvaTaux: bdc.tva_taux,
  description: bdc.description,
  modePaiement: bdc.mode_paiement,
  insertQuery: insertQuery,
  updateQuery: updateQuery
} }];

var invoice = $('08. Create Invoice').first().json;
var rawLines = [];
try { rawLines = $('09. Get BdC Lines').all().map(function(item) { return item.json; }); } catch(e) { rawLines = []; }

function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }
function num(v) { var n = parseFloat(v); return isNaN(n) ? '0' : String(n); }

if (rawLines.length === 0) {
  return [{ json: { invoiceId: invoice.id, hasLines: false, query: 'SELECT 1' } }];
}

var values = rawLines.map(function(item, i) {
  var q = num(item.quantite || 0);
  var p = num(item.prix_unitaire_ht || 0);
  var t = num(item.total_ht || (parseFloat(q) * parseFloat(p)));
  return '(gen_random_uuid(), ' + esc(invoice.id) + '::uuid, ' + esc(item.code_article || null) + ', ' + esc(item.designation || '') + ', ' + q + ', ' + p + ', ' + t + ', ' + i + ')';
}).join(', ');

var query = 'INSERT INTO invoice_lines (id, invoice_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre) VALUES ' + values + ' RETURNING id, invoice_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre';

return [{ json: { invoiceId: invoice.id, hasLines: true, query: query } }];

var invoice = $('08. Create Invoice').first().json;
var bdc = $('12. Update BdC').first().json;

return [{ json: {
  success: true,
  invoice_id: invoice.id,
  numero_facture: invoice.numero_facture,
  bdc_id: bdc.id,
  numero_bdc: bdc.numero_bdc,
  bdc_statut: bdc.statut
} }];
