const payload = $input.body;

if (!payload.tenantId || !payload.clientFinalId || payload.montantHt === undefined) {
  throw new Error('tenantId, clientFinalId, and montantHt are required');
}

const tvaTaux = parseFloat(payload.tvaTaux) || 0;
const montantHt = parseFloat(payload.montantHt);
const montantTtc = montantHt * (1 + tvaTaux / 100);
const dateBdc = payload.dateBdc || new Date().toISOString();
const dateValidite = payload.dateValidite || null;
const description = payload.description || null;
const modePaiement = payload.modePaiement || null;
const lines = payload.lines || [];

return {
  tenantId: payload.tenantId,
  clientFinalId: payload.clientFinalId,
  montantHt: montantHt,
  montantTtc: montantTtc,
  tvaTaux: tvaTaux,
  dateBdc: dateBdc,
  dateValidite: dateValidite,
  description: description,
  modePaiement: modePaiement,
  lines: lines
};

const nextNum = $('05. Get Next Numero').first().next_num;
const year = new Date().getFullYear();
const numeroBdc = `BDC-${year}-${String(nextNum).padStart(6, '0')}`;

return {
  numeroBdc: numeroBdc
};

parameters.jsCode:
const bon = $('07. Insert Bon').first().json;
const inputLines = $json.lines || [];
const bonId = bon.id;

if (!inputLines || inputLines.length === 0) {
  return { bonId, lines: [], hasLines: false };
}

const lines = inputLines.map((line, idx) => ({
  codeArticle: line.codeArticle || line.code_article || '',
  designation: line.designation || line.description || '',
  quantite: line.quantite || 1,
  prixUnitaireHt: parseFloat(line.prixUnitaireHt || line.prix_unitaire_ht || line.prixUnitaire || 0),
  totalHt: parseFloat(line.totalHt || line.total_ht || line.montant || 0),
  ordre: idx
}));

return { bonId, lines, hasLines: true };

