var body = $input.first().json.body || $input.first().json;

if (!body.tenantId) {
  throw new Error('tenantId is required');
}
if (!body.clientFinalId) {
  throw new Error('clientFinalId is required');
}

var montantHt = parseFloat(body.montantHt) || 0;
var tvaTaux = parseFloat(body.tvaTaux) || 20;
var montantTtc = Math.round(montantHt * (1 + tvaTaux / 100) * 100) / 100;

return [{ json: {
  tenantId: body.tenantId,
  clientFinalId: body.clientFinalId,
  montantHt,
  montantTtc,
  tvaTaux,
  dateBdc: body.dateBdc || new Date().toISOString(),
  dateValidite: body.dateValidite || null,
  description: body.description || null,
  modePaiement: body.modePaiement || null,
  lines: body.lines || []
} }];

var parser = $('02. Parser').first().json;
var nextNum = $('05. Get Next Number').first().json.next_num || 1;
var year = new Date().getFullYear();
var numeroBdc = 'BDC-' + year + '-' + String(nextNum).padStart(6, '0');

return [{ json: { ...parser, numeroBdc } }];

var d = $json;
function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }

var clientPart = d.clientFinalId ? esc(d.clientFinalId) : 'NULL';
var descPart = d.description ? esc(d.description) : 'NULL';
var modePart = d.modePaiement ? esc(d.modePaiement) : 'NULL';
var validitePart = d.dateValidite ? esc(d.dateValidite) : 'NULL';

var query = 'INSERT INTO bons_commande (id, tenant_id, client_final_id, numero_bdc, date_bdc, date_validite, montant_ht, montant_ttc, tva_taux, description, mode_paiement, statut, created_at, updated_at) VALUES (gen_random_uuid(), ' + esc(d.tenantId) + '::uuid, ' + clientPart + ', ' + esc(d.numeroBdc) + ', ' + esc(d.dateBdc) + ', ' + validitePart + ', ' + d.montantHt + ', ' + d.montantTtc + ', ' + (d.tvaTaux || 20) + ', ' + descPart + ', ' + modePart + ', ' + "'brouillon'" + ', NOW(), NOW()) RETURNING *';

return [{ json: { query, lines: d.lines || [] } }];

var bdc = $json;
var lines = $('07. Build Insert SQL').first().json.lines || [];

if (lines.length === 0) {
  return [{ json: { bdcId: bdc.id, hasLines: false, query: 'SELECT 1' } }];
}

function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }

var values = lines.map(function(line, i) {
  var parentId = esc(bdc.id);
  var codeArticle = line.codeArticle ? esc(line.codeArticle) : 'NULL';
  var designation = esc(String(line.designation || '').substring(0, 500));
  var quantite = parseFloat(line.quantite) || 1;
  var prixUnitaireHt = parseFloat(line.prixUnitaireHt || line.prixUnitaireHT) || 0;
  var totalHt = Math.round(quantite * prixUnitaireHt * 100) / 100;
  var ordre = i;
  return '(gen_random_uuid(), ' + parentId + ', ' + codeArticle + ', ' + designation + ', ' + quantite + ', ' + prixUnitaireHt + ', ' + totalHt + ', ' + ordre + ')';
}).join(', ');

var query = 'INSERT INTO bon_commande_lines (id, bon_commande_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre) VALUES ' + values + ' RETURNING id, bon_commande_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre';

return [{ json: { bdcId: bdc.id, hasLines: true, query: query } }];
