var body = $input.first().json.body || $input.first().json;

if (!body.tenantId) {
  throw new Error('tenantId is required');
}

var page = parseInt(body.page) || 1;
var limit = parseInt(body.limit) || 10;
var offset = (page - 1) * limit;
var statut = body.statut || null;
var clientFinalId = body.clientFinalId || null;

return [{ json: { tenantId: body.tenantId, page, limit, offset, statut, clientFinalId } }];

var bdcItems = $('06. List Query').all().map(function(item) { return item.json; }).filter(function(b) { return b.id; });
var bdcIds = bdcItems.map(function(b) { return "'" + b.id + "'"; });
var idsClause = bdcIds.length > 0 ? bdcIds.join(',') : "'00000000-0000-0000-0000-000000000000'";
var query = 'SELECT id, bon_commande_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre FROM bon_commande_lines WHERE bon_commande_id IN (' + idsClause + ') ORDER BY ordre ASC';
return [{ json: { query } }];

var items = $('06. List Query').all().map(function(item) { return item.json; }).filter(function(b) { return b.id; });
var linesRaw = $('06c. Lines Query').all().map(function(item) { return item.json; }).filter(function(l) { return l.bon_commande_id; });
var countResult = $('05. Count Query').first().json;
var parser = $('02. Parser').first().json;
var total = parseInt(countResult.total) || 0;
var totalPages = Math.ceil(total / parser.limit) || 1;

// Grouper les lignes par bon_commande_id
var linesByBdcId = {};
for (var i = 0; i < linesRaw.length; i++) {
  var l = linesRaw[i];
  var bid = l.bon_commande_id;
  if (!linesByBdcId[bid]) linesByBdcId[bid] = [];
  linesByBdcId[bid].push({
    id: l.id,
    bonCommandeId: l.bon_commande_id,
    codeArticle: l.code_article || null,
    designation: l.designation || '',
    quantite: parseFloat(l.quantite) || 1,
    prixUnitaireHt: parseFloat(l.prix_unitaire_ht) || 0,
    totalHt: parseFloat(l.total_ht) || 0,
    ordre: parseInt(l.ordre) || 0
  });
}

var bons = items.map(function(b) { return ({
  id: b.id,
  tenant_id: b.tenant_id,
  client_final_id: b.client_final_id,
  numero_bdc: b.numero_bdc,
  numeroBdc: b.numero_bdc,
  date_bdc: b.date_bdc,
  dateBdc: b.date_bdc,
  date_validite: b.date_validite,
  dateValidite: b.date_validite,
  montant_ht: b.montant_ht,
  montantHt: b.montant_ht,
  montant_ttc: b.montant_ttc,
  montantTtc: b.montant_ttc,
  tva_taux: b.tva_taux,
  tvaTaux: b.tva_taux,
  description: b.description,
  mode_paiement: b.mode_paiement,
  modePaiement: b.mode_paiement,
  statut: b.statut,
  invoice_id: b.invoice_id,
  invoiceId: b.invoice_id,
  created_at: b.created_at,
  createdAt: b.created_at,
  updated_at: b.updated_at,
  updatedAt: b.updated_at,
  clientFinal: b.client_final_id ? {
    id: b.client_final_id,
    nom: b.client_nom || null,
    prenom: b.client_prenom || null,
    email: b.client_email || null,
    telephone: b.client_telephone || null,
    raisonSociale: b.client_raison_sociale || null
  } : null,
  lines: linesByBdcId[b.id] || []
}); });

return [{ json: {
  bons,
  count: bons.length,
  total,
  page: parser.page,
  limit: parser.limit,
  totalPages
}}];
