const payload = $input.body;

if (!payload.tenantId) {
  throw new Error('tenantId is required');
}

const page = parseInt(payload.page) || 1;
const limit = parseInt(payload.limit) || 10;
const offset = (page - 1) * limit;
const statut = payload.statut || null;
const clientFinalId = payload.clientFinalId || null;

return {
  tenantId: payload.tenantId,
  page,
  limit,
  offset,
  statut,
  clientFinalId
};

const rawBons = $('06. List Query').all().map(item => item.json);
const countResult = $('05. Count Query').first().json;
const total = parseInt(countResult.total) || 0;
const totalPages = Math.ceil(total / $('02. Parser').first().json.limit) || 1;

// Nest client data into clientFinal object
const bons = rawBons.map(b => ({
  ...b,
  clientFinal: b.client_final_id ? {
    id: b.client_final_id,
    nom: b.client_nom || null,
    prenom: b.client_prenom || null,
    email: b.client_email || null,
    telephone: b.client_telephone || null,
    raisonSociale: b.client_raison_sociale || null,
  } : null,
}));

return [{json: {
  bons,
  count: bons.length,
  total,
  page: $('02. Parser').first().json.page,
  limit: $('02. Parser').first().json.limit,
  totalPages,
}}];
