var body = $input.first().json.body || $input.first().json;

if (!body.tenantId) {
  throw new Error('tenantId is required');
}
if (!body.bdcId) {
  throw new Error('bdcId is required');
}

var montantHt = 0;
var lines = body.lines || [];
if (lines.length > 0) {
  for (var i = 0; i < lines.length; i++) {
    var q = parseFloat(lines[i].quantite) || 1;
    var p = parseFloat(lines[i].prixUnitaireHt || lines[i].prixUnitaireHT) || 0;
    montantHt += q * p;
  }
  montantHt = Math.round(montantHt * 100) / 100;
}

var tvaTaux = parseFloat(body.tvaTaux) || 20;
var montantTtc = Math.round(montantHt * (1 + tvaTaux / 100) * 100) / 100;

return [{ json: {
  tenantId: body.tenantId,
  bdcId: body.bdcId,
  clientFinalId: body.clientFinalId || null,
  montantHt,
  montantTtc,
  tvaTaux,
  dateBdc: body.dateBdc || null,
  dateValidite: body.dateValidite || null,
  description: body.description || null,
  modePaiement: body.modePaiement || null,
  hasNewLines: lines.length > 0,
  lines: lines
} }];

var d = $json;
function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }
function num(v) { var n = parseFloat(v); return isNaN(n) ? '0' : String(n); }

var setClauses = [];
if (d.clientFinalId) setClauses.push('client_final_id = ' + esc(d.clientFinalId) + '::uuid');
if (d.dateBdc) setClauses.push('date_bon_commande = ' + esc(d.dateBdc));
if (d.dateValidite) setClauses.push('date_validite = ' + esc(d.dateValidite));
if (d.description !== undefined) setClauses.push('description = ' + esc(d.description));
if (d.modePaiement !== undefined) setClauses.push('mode_paiement = ' + esc(d.modePaiement));
setClauses.push('tva_taux = ' + num(d.tvaTaux));
if (d.hasNewLines) {
  setClauses.push('montant_ht = ' + num(d.montantHt));
  setClauses.push('montant_ttc = ' + num(d.montantTtc));
}
setClauses.push('updated_at = NOW()');

var query = 'UPDATE bon_commande SET ' + setClauses.join(', ') + ' WHERE id = ' + esc(d.bdcId) + '::uuid AND tenant_id = ' + esc(d.tenantId) + '::uuid RETURNING *';

return [{ json: { query, bdcId: d.bdcId, hasNewLines: d.hasNewLines, lines: d.lines } }];

var prev = $('05. Build Update SQL').first().json;
var bdcId = prev.bdcId;
var hasNewLines = prev.hasNewLines;
var lines = prev.lines || [];

if (!hasNewLines || lines.length === 0) {
  return [{ json: { bdcId, skipped: true, query: 'SELECT 1' } }];
}

var deleteQuery = "DELETE FROM bon_commande_lines WHERE bon_commande_id = '" + bdcId + "'::uuid";

return [{ json: { bdcId, skipped: false, deleteQuery, lines } }];

var prev = $('07. Prepare Lines Delete').first().json;
var bdcId = prev.bdcId;
var lines = prev.lines || [];

if (prev.skipped || lines.length === 0) {
  return [{ json: { bdcId, query: 'SELECT 1' } }];
}

function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }
function num(v) { var n = parseFloat(v); return isNaN(n) ? '0' : String(n); }

var values = lines.map(function(line, idx) {
  var q = num(line.quantite || 1);
  var p = num(line.prixUnitaireHt || line.prixUnitaireHT || 0);
  var t = num(parseFloat(q) * parseFloat(p));
  return '(gen_random_uuid(), ' + esc(bdcId) + '::uuid, ' + esc(line.codeArticle || null) + ', ' + esc(line.designation || '') + ', ' + q + ', ' + p + ', ' + t + ', ' + idx + ')';
}).join(', ');

var query = 'INSERT INTO bon_commande_lines (id, bon_commande_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre) VALUES ' + values + ' RETURNING *';

return [{ json: { bdcId, query } }];
