var raw=$input.first().json;
function pj(x){if(typeof x!=='string')return x;try{return JSON.parse(x)}catch(e){return null}}
function isO(v){return v!==null&&typeof v==='object'&&!Array.isArray(v)}
function g(o,k1,k2){if(!isO(o))return null;var v=o[k1];if(v!=null&&v!=='')return v;if(k2){v=o[k2];if(v!=null&&v!=='')return v}return null}
var payload=isO(raw)?raw:{};
if(isO(raw)&&raw.body!==undefined)payload=typeof raw.body==='string'?pj(raw.body):raw.body;
if(!isO(payload))payload={};
var data=isO(payload.data)?payload.data:payload;
if(!isO(data))data=payload;
var tenantId=g(payload,'tenantId','tenant_id')||g(data,'tenantId','tenant_id')||g(raw,'tenantId','tenant_id');
var clientFinalId=g(payload,'clientFinalId','client_final_id')||g(data,'clientFinalId','client_final_id');
var raisonSociale=g(payload,'raisonSociale','raison_sociale')||g(data,'raisonSociale','raison_sociale')||'';
var nom=g(payload,'nom')||g(data,'nom')||'';
var prenom=g(payload,'prenom')||g(data,'prenom')||'';
var modulesInclus=payload.modulesInclus||data.modulesInclus||payload.modules_inclus||data.modules_inclus||[];
if(!tenantId||!clientFinalId)return [{json:{_error:true,message:'tenantId et clientFinalId requis'}}];
// Generate slug from raison sociale or nom/prenom
var rawName=raisonSociale||((nom+' '+prenom).trim())||'client';
var slug=rawName.toLowerCase().replace(/[^a-z0-9]+/g,'-').replace(/^-|-$/g,'').substring(0,50);
var tenantSlug=slug+'-'+clientFinalId.substring(0,8);
return [{json:{tenantId,clientFinalId,raisonSociale,nom,prenom,modulesInclus,tenantSlug}}];

// SSH placeholder - folder creation will be done when SSH credential is configured
var p=$('02. Parser').first().json;
var folderPath='/var/www/talosprimes/clients/'+p.tenantSlug;
// TODO: Replace with SSH node when credential b7jMAjh1KJcIwqdo is configured
// Command: mkdir -p {folderPath}/{documents,exports,uploads,config} && chmod -R 750 {folderPath}
return [{json:{...p,folderPath,sshSkipped:true}}];

var p = $('02. Parser').first().json;
var query = `INSERT INTO client_spaces (id, tenant_id, client_final_id, email, token, statut, date_expiration, created_at) VALUES (gen_random_uuid(), '${p.tenantId}'::uuid, '${p.clientFinalId}'::uuid, '${p.email}', '${p.token}', '${p.statut || 'en_attente'}', '${p.dateExpiration || new Date(Date.now() + 30*24*60*60*1000).toISOString()}', NOW()) RETURNING *`;
return [{json:{query}}];

var p = $input.item.json;
var query = `INSERT INTO notifications (id, tenant_id, titre, message, type, lu, created_at) VALUES (gen_random_uuid(), '${p.tenantId}', '${p.tenantSlug}', '${(p.modules||[]}', $4, false, NOW())`;
return [{json:{query}}];

