const raw=$input.first().json;
function pj(x){if(typeof x!=='string')return x;try{return JSON.parse(x)}catch(e){return null}}
function isO(v){return v!==null&&typeof v==='object'&&!Array.isArray(v)}
function g(o,k1,k2){if(!isO(o))return null;var v=o[k1];if(v!=null&&v!=='')return v;if(k2){v=o[k2];if(v!=null&&v!=='')return v}return null}
let payload=isO(raw)?raw:{};
if(isO(raw)&&raw.body!==undefined)payload=typeof raw.body==='string'?pj(raw.body):raw.body;
if(!isO(payload))payload={};
let data=isO(payload.data)?payload.data:payload;
if(!isO(data))data=payload;
const tenantId=g(payload,'tenantId','tenant_id')||g(data,'tenantId','tenant_id')||g(raw,'tenantId','tenant_id');
const clientFinalId=g(payload,'clientFinalId','client_final_id')||g(data,'clientFinalId','client_final_id');
const raisonSociale=g(payload,'raisonSociale','raison_sociale')||g(data,'raisonSociale','raison_sociale')||'';
const nom=g(payload,'nom')||g(data,'nom')||'';
const prenom=g(payload,'prenom')||g(data,'prenom')||'';
const modulesInclus=payload.modulesInclus||data.modulesInclus||payload.modules_inclus||data.modules_inclus||[];
if(!tenantId||!clientFinalId)return [{json:{_error:true,message:'tenantId et clientFinalId requis'}}];
// Generate slug from raison sociale or nom/prenom
const rawName=raisonSociale||((nom+' '+prenom).trim())||'client';
const slug=rawName.toLowerCase().replace(/[^a-z0-9]+/g,'-').replace(/^-|-$/g,'').substring(0,50);
const tenantSlug=slug+'-'+clientFinalId.substring(0,8);
return [{json:{tenantId,clientFinalId,raisonSociale,nom,prenom,modulesInclus,tenantSlug}}];

// SSH placeholder - folder creation will be done when SSH credential is configured
const p=$('02. Parser').first().json;
const folderPath='/var/www/talosprimes/clients/'+p.tenantSlug;
// TODO: Replace with SSH node when credential b7jMAjh1KJcIwqdo is configured
// Command: mkdir -p {folderPath}/{documents,exports,uploads,config} && chmod -R 750 {folderPath}
return [{json:{...p,folderPath,sshSkipped:true}}];

const p=$('02. Parser').first().json;
var esc=function(s){return (s||'').replace(/'/g,"''");};
const folderPath='/var/www/talosprimes/clients/'+p.tenantSlug;
const modules=Array.isArray(p.modulesInclus)?p.modulesInclus:[];
const modulesArr=modules.map(function(m){return "'"+esc(m)+"'"}).join(',');
var query="INSERT INTO client_spaces (id, tenant_id, client_final_id, tenant_slug, folder_path, status, modules_actives, created_at, updated_at) VALUES (gen_random_uuid(), '"+esc(p.tenantId)+"', '"+esc(p.clientFinalId)+"', '"+esc(p.tenantSlug)+"', '"+esc(folderPath)+"', 'en_attente_validation', ARRAY["+modulesArr+"]::text[], NOW(), NOW()) RETURNING id, tenant_slug, status";
return [{json:{query,tenantId:p.tenantId,clientFinalId:p.clientFinalId,tenantSlug:p.tenantSlug,folderPath,modules}}];

const p=$('06. Construire INSERT').first().json;
var esc=function(s){return (s||'').replace(/'/g,"''");};
var query="INSERT INTO notifications (id, tenant_id, titre, message, type, lu, created_at) VALUES (gen_random_uuid(), '"+esc(p.tenantId)+"', 'Nouvel espace client', 'L''espace client "+esc(p.tenantSlug)+" est prêt à valider. Modules: "+esc((p.modules||[]).join(', '))+"', 'info', false, NOW())";
return [{json:{query}}];

