const raw=$input.first().json;
function pj(x){if(typeof x!=='string')return x;try{return JSON.parse(x)}catch(e){return null}}
function isO(v){return v!==null&&typeof v==='object'&&!Array.isArray(v)}
function g(o,k1,k2){if(!isO(o))return null;var v=o[k1];if(v!=null&&v!=='')return v;if(k2){v=o[k2];if(v!=null&&v!=='')return v}return null}
let payload=isO(raw)?raw:{};
if(isO(raw)&&raw.body!==undefined)payload=typeof raw.body==='string'?pj(raw.body):raw.body;
if(!isO(payload))payload={};
let data=isO(payload.data)?payload.data:payload;
if(!isO(data))data=payload;
const tenantId=g(payload,'tenantId','tenant_id')||g(data,'tenantId','tenant_id')||g(raw,'tenantId','tenant_id');
const status=g(payload,'status')||g(data,'status')||'';
if(!tenantId)return [{json:{_error:true,message:'tenantId requis'}}];
return [{json:{tenantId,status}}];

const p=$('02. Parser').first().json;
var esc=function(s){return (s||'').replace(/'/g,"''");};
var statusFilter=p.status?" AND cs.status = '"+esc(p.status)+"'":'';
var query="SELECT COALESCE(json_agg(row_to_json(sub)), '[]'::json) AS spaces FROM (SELECT cs.id, cs.tenant_id, cs.client_final_id, cs.client_tenant_id, cs.tenant_slug, cs.folder_path, cs.status, cs.modules_actives, cs.validated_at, cs.validated_by_user_id, cs.created_at, cs.updated_at, cf.nom AS client_nom, cf.prenom AS client_prenom, cf.raison_sociale, cf.email AS client_email, cf.telephone AS client_telephone FROM client_spaces cs JOIN client_finals cf ON cf.id = cs.client_final_id WHERE cs.tenant_id = '"+esc(p.tenantId)+"'"+statusFilter+" ORDER BY cs.created_at DESC) sub";
return [{json:{query}}];

const items=$input.all();
if(!items||items.length===0||Object.keys(items[0].json||{}).length===0){
  return [{json:{spaces:[]}}];
}
const row=$input.first().json;
var spaces=row.spaces||[];
if(typeof spaces==='string')spaces=JSON.parse(spaces);
return [{json:{spaces}}];
