const raw=$input.first().json;
function pj(x){if(typeof x!=='string')return x;try{return JSON.parse(x)}catch(e){return null}}
function isO(v){return v!==null&&typeof v==='object'&&!Array.isArray(v)}
function g(o,k1,k2){if(!isO(o))return null;var v=o[k1];if(v!=null&&v!=='')return v;if(k2){v=o[k2];if(v!=null&&v!=='')return v}return null}
let payload=isO(raw)?raw:{};
if(isO(raw)&&raw.body!==undefined)payload=typeof raw.body==='string'?pj(raw.body):raw.body;
if(!isO(payload))payload={};
let data=isO(payload.data)?payload.data:payload;
if(!isO(data))data=payload;
const tenantId=g(payload,'tenantId','tenant_id')||g(data,'tenantId','tenant_id')||g(raw,'tenantId','tenant_id');
const clientSpaceId=g(payload,'clientSpaceId','client_space_id')||g(data,'clientSpaceId','client_space_id');
if(!tenantId||!clientSpaceId)return [{json:{_error:true,message:'tenantId et clientSpaceId requis'}}];
return [{json:{tenantId,clientSpaceId}}];

const p=$('02. Parser').first().json;
var esc=function(s){return (s||'').replace(/'/g,"''");};
var query="SELECT cs.id, cs.tenant_id, cs.status, cs.tenant_slug, cf.email, cf.nom, cf.prenom, cf.raison_sociale FROM client_spaces cs JOIN client_finals cf ON cf.id = cs.client_final_id WHERE cs.id = '"+esc(p.clientSpaceId)+"' AND cs.tenant_id = '"+esc(p.tenantId)+"' AND cs.status = 'actif'";
return [{json:{query}}];

const space=$input.first().json;
if(!space||!space.id)return [{json:{_error:true,message:'Espace client introuvable ou non actif'}}];
// Generate new temp password
const chars='ABCDEFGHJKLMNPQRSTUVWXYZabcdefghijkmnpqrstuvwxyz23456789!@#$';
let pwd='';
for(let i=0;i<12;i++)pwd+=chars.charAt(Math.floor(Math.random()*chars.length));
return [{json:{...space,tempPassword:pwd}}];
