// Parser et valider le payload du webhook
const raw = $input.first().json;
const payload = (raw && typeof raw === 'object' && 'body' in raw) ? raw.body : raw;
const data = (payload && typeof payload === 'object' && 'data' in payload) ? payload.data : payload;

// Extraire le tenantId depuis le payload webhook
const tenantId = payload?.tenantId || raw?.tenantId || '';
const type = data?.type || '';
const email = data?.email || '';
const raisonSociale = data?.raisonSociale || '';
const nom = data?.nom || '';
const prenom = data?.prenom || '';
const telephone = data?.telephone || '';
const adresse = data?.adresse || '';
const tags = Array.isArray(data?.tags) ? data.tags : [];

// Validation
if (!tenantId) {
  throw new Error('Tenant ID requis');
}

if (!type || !['b2b', 'b2c'].includes(type)) {
  throw new Error('Type de client requis (b2b ou b2c)');
}

if (!email) {
  throw new Error('Email requis');
}

if (type === 'b2b' && !raisonSociale) {
  throw new Error('Raison sociale requise pour un client B2B');
}

if (type === 'b2c' && (!nom || !prenom)) {
  throw new Error('Nom et prénom requis pour un client B2C');
}

return [{ json: { tenantId, type, email, raisonSociale, nom, prenom, telephone, adresse, tags } }];
