var body = $input.first().json.body || $input.first().json;
var data = body.data || body;
return [{json: {
  tenantId: data.tenantId || '00000000-0000-0000-0000-000000000001',
  type: data.type || 'b2c',
  nom: data.nom || '',
  prenom: data.prenom || '',
  email: data.email || '',
  telephone: data.telephone || '',
  raison_sociale: data.raisonSociale || data.raison_sociale || '',
  adresse: data.adresse || ''
}}];

var d = $json;
function esc(v) { return v == null ? '' : String(v).replace(/'/g, "''"); }
var rs = d.raison_sociale ? "'" + esc(d.raison_sociale) + "'" : 'NULL';
var adr = d.adresse ? "'" + esc(d.adresse) + "'" : 'NULL';
var query = "INSERT INTO client_finals (id, tenant_id, type, raison_sociale, nom, prenom, email, telephone, adresse, statut, created_at, updated_at) VALUES (gen_random_uuid(), '" + esc(d.tenantId) + "'::uuid, '" + esc(d.type) + "', " + rs + ", '" + esc(d.nom) + "', '" + esc(d.prenom) + "', '" + esc(d.email) + "', '" + esc(d.telephone) + "', " + adr + ", 'actif', NOW(), NOW()) ON CONFLICT (tenant_id, email) DO UPDATE SET nom = EXCLUDED.nom, prenom = EXCLUDED.prenom, telephone = EXCLUDED.telephone, raison_sociale = COALESCE(NULLIF(EXCLUDED.raison_sociale, ''), client_finals.raison_sociale), adresse = COALESCE(NULLIF(EXCLUDED.adresse, ''), client_finals.adresse), updated_at = NOW() RETURNING *";
return [{json: {query}}];
