// Préparer les données pour l'onboarding
// Le webhook reçoit les données dans body ou directement
const raw = $input.item.json;
const payload = raw?.body || raw?.json || raw;

// Le backend envoie : { event, tenantId, timestamp, data: { client: {...}, plan: {...}, avecStripe } }
const tenantId = payload.tenantId || payload.data?.tenantId;
const clientData = payload.data?.client || payload.client || payload.data || {};
const clientId = clientData.id || clientData.clientId;

// Plan : utiliser celui du payload si présent, sinon valeurs par défaut
const planFromPayload = payload.data?.plan || payload.plan || {};
const plan = {
  nomPlan: planFromPayload.nomPlan || "Plan Starter",
  montantMensuel: planFromPayload.montantMensuel || 29.99,
  modulesInclus: planFromPayload.modulesInclus || ["gestion_clients", "facturation", "suivi"],
  dureeMois: planFromPayload.dureeMois || 1
};

// Option Stripe
const avecStripe = payload.data?.avecStripe || payload.avecStripe || false;

// Validation
if (!tenantId || !clientId) {
  throw new Error(`Données manquantes : tenantId=${tenantId ? 'OK' : 'MANQUANT'}, clientId=${clientId ? 'OK' : 'MANQUANT'}`);
}

// Calculer les dates
const dateDebut = new Date();
const dateProchainRenouvellement = new Date();
dateProchainRenouvellement.setMonth(dateProchainRenouvellement.getMonth() + plan.dureeMois);

return {
  json: {
    tenantId,
    clientId,
    clientData,
    plan,
    avecStripe,
    dateDebut: dateDebut.toISOString(),
    dateProchainRenouvellement: dateProchainRenouvellement.toISOString(),
    statut: "actif"
  }
};

// Préparer les URLs de redirection pour Stripe Checkout
const prepareData = $('01. Préparer données onboarding').item.json;
const baseUrl = 'https://talosprimes.com';
const clientId = prepareData.clientId;

// URLs de redirection après paiement
const successUrl = `${baseUrl}/clients?checkout=success&clientId=${clientId}&session_id={CHECKOUT_SESSION_ID}`;
const cancelUrl = `${baseUrl}/clients?checkout=cancelled&clientId=${clientId}`;

return {
  json: {
    ...prepareData,
    checkoutSuccessUrl: successUrl,
    checkoutCancelUrl: cancelUrl
  }
};

// Préparer les données avec les IDs Stripe et l'URL de checkout
const customerId = $('04. Stripe - Créer Customer').item.json.id;
const checkoutSession = $('07. Stripe - Créer Session Checkout').item.json;
const checkoutUrl = checkoutSession.url;
const prepareData = $('01. Préparer données onboarding').item.json;

return {
  json: {
    ...prepareData,
    idClientStripe: customerId,
    idSessionCheckoutStripe: checkoutSession.id,
    checkoutUrl: checkoutUrl
  }
};

// Préparer les données après création de l'abonnement
// Le Merge combine les données de deux sources :
// - Input 0 : résultat de la requête PostgreSQL (abonnement créé)
// - Input 1 : données conservées de 09c (toutes les infos)

const allItems = $input.all();

// Trouver l'abonnement (résultat PostgreSQL)
const subscriptionItem = allItems.find(item => item.json[0]?.id || item.json.id) || allItems[0];
const subscriptionData = subscriptionItem.json[0] || subscriptionItem.json;

// Trouver les données conservées (clientId, tenantId, plainPassword, etc.)
const dataItem = allItems.find(item => item.json.clientId && item.json.tenantId) || allItems[1] || {};
const savedData = dataItem.json || {};

// Combiner toutes les données
const combinedData = {
  ...savedData,
  subscriptionData: subscriptionData,
  subscriptionId: subscriptionData.id
};

return {
  json: combinedData
};

// Préparer les modules inclus pour la requête SQL paramétrisée
const inputItems = $input.all();

// Trouver les données principales
const mainData = inputItems.find(item => item.json.plan || item.json.clientId) || inputItems[0] || {};
const passwordData = inputItems.find(item => item.json.plainPassword) || {};

const data = {
  ...mainData.json,
  ...passwordData.json
};

const modules = data.plan?.modulesInclus || data.modulesInclus || [];
// Pour PG: passer comme array literal {mod1,mod2}
const modulesLiteral = '{' + modules.map(m => String(m)).join(',') + '}';

const query = `INSERT INTO client_subscriptions (
  id, client_final_id, nom_plan, date_debut, date_prochain_renouvellement,
  montant_mensuel, modules_inclus, statut, id_client_stripe,
  id_abonnement_stripe, temporary_password, updated_at
) VALUES (
  gen_random_uuid(), $1::uuid, $2, $3::timestamptz, $4::timestamptz,
  $5, $6::text[], 'actif', $7, $8, $9, NOW()
) RETURNING *;`;

const params = [
  data.clientId,
  String(data.plan.nomPlan),
  data.dateDebut,
  data.dateProchainRenouvellement,
  data.plan.montantMensuel,
  modulesLiteral,
  data.idClientStripe || null,
  data.idAbonnementStripe || null,
  data.plainPassword || null
];

return {
  json: {
    query,
    params,
    allData: data
  }
};

// Conserver toutes les données de préparation pour les nœuds suivants
// Car après l'exécution SQL, ces données seront perdues
const allData = $input.item.json.allData || $input.item.json;

return {
  json: allData
};

// Générer un mot de passe sécurisé aléatoire et l'ajouter aux données
const generatePassword = () => {
  const length = 12;
  const charset = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*';
  let password = '';
  for (let i = 0; i < length; i++) {
    password += charset.charAt(Math.floor(Math.random() * charset.length));
  }
  return password;
};

const prepareData = $input.item.json;
const clientData = prepareData.clientData || {};

// Générer le mot de passe
const plainPassword = generatePassword();

// Nom du tenant (basé sur le nom du client)
const tenantName = clientData.raisonSociale || `${clientData.nom || ''} ${clientData.prenom || ''}`.trim() || clientData.email || 'Client';

// Retourner toutes les données avec le mot de passe ajouté
return {
  json: {
    ...prepareData,
    tenantName: tenantName,
    clientEmail: clientData.email || '',
    plainPassword: plainPassword,
    clientNom: clientData.nom || '',
    clientPrenom: clientData.prenom || '',
    clientRaisonSociale: clientData.raisonSociale || null
  }
};

// Préparer les données pour créer l'espace client (dossier VPS + enregistrement BDD)
// La création du Tenant+User est reportée à la validation admin
const data = $input.item.json;

const clientId = data.clientId || data.subscriptionData?.client_final_id;
const tenantId = data.tenantId;
const nom = data.clientNom || data.clientData?.nom || '';
const prenom = data.clientPrenom || data.clientData?.prenom || '';
const raisonSociale = data.clientRaisonSociale || data.clientData?.raisonSociale || '';
const modulesInclus = data.plan?.modulesInclus || data.subscriptionData?.modules_inclus || [];

if (!clientId || !tenantId) {
  throw new Error('Données manquantes pour créer espace client: clientId ou tenantId');
}

return {
  json: {
    tenantId,
    clientFinalId: clientId,
    raisonSociale,
    nom,
    prenom,
    modulesInclus
  }
};

