// Préparer la réponse de succès
// Récupérer la réponse de création d'espace client (10c)
const spaceResponse = $input.item.json;
const spaceData = spaceResponse.data || spaceResponse || {};

// Récupérer les données depuis 10a via le contexte du workflow
const prepareData = $node['10a. Préparer après abonnement'].json || {};

// Données client depuis prepareData
const clientData = prepareData.clientData || {};

// Vérifier si Stripe est activé
const avecStripe = prepareData.idClientStripe || prepareData.idSessionCheckoutStripe || prepareData.checkoutUrl || prepareData.avecStripe;
const stripe = avecStripe && prepareData.checkoutUrl ? {
  customerId: prepareData.idClientStripe,
  checkoutSessionId: prepareData.idSessionCheckoutStripe,
  checkoutUrl: prepareData.checkoutUrl,
  requiresPayment: true
} : null;

return {
  json: {
    success: true,
    message: stripe && stripe.checkoutUrl 
      ? "Espace client créé — en attente de validation admin. Redirection vers le paiement..." 
      : "Espace client créé — en attente de validation par l'administrateur. Les identifiants seront envoyés après validation.",
    client: clientData,
    subscription: prepareData.subscriptionData || null,
    modulesActives: prepareData.subscriptionData?.modules_inclus || prepareData.plan?.modulesInclus || [],
    clientSpace: {
      id: spaceData.id || null,
      tenantSlug: spaceData.tenantSlug || null,
      status: 'en_attente_validation',
      folderPath: spaceData.folderPath || null,
      modules: spaceData.modules || []
    },
    credentials: null,
    stripe: stripe
  }
};

// Préparer le body JSON pour la notification
const responseData = $input.item.json;
const subscription = responseData.subscription;
const client = responseData.client || {};
const clientSpace = responseData.clientSpace || {};

let tenantId, clientId;
try {
  const prepareData = $node['10a. Préparer après abonnement'].json || {};
  tenantId = prepareData.tenantId;
  clientId = prepareData.clientId;
} catch (e) {
  tenantId = clientSpace.tenantSlug;
  clientId = client.id;
}

const clientName = client.prenom || client.nom || client.raisonSociale || 'le client';
const titreStripe = responseData.stripe ? ' (Stripe)' : '';
const messageStripe = responseData.stripe ? ' - Abonnement Stripe créé' : '';

return {
  json: {
    type: 'client_onboarding',
    titre: `Espace client en attente de validation${titreStripe}`,
    message: `L'espace client pour ${clientName} a été créé et attend votre validation. Les identifiants seront envoyés après validation${messageStripe}`,
    donnees: {
      tenantId: tenantId,
      clientId: clientId,
      clientSpaceId: clientSpace.id || null,
      subscriptionId: subscription?.id || subscription?.[0]?.id
    }
  }
};

const d = $input.item.json;
var query = "INSERT INTO notifications (id, tenant_id, titre, message, type, lu, created_at) VALUES (gen_random_uuid(), $1, $2, $3, $4, false, NOW())";
var params = [s, d.titre || 'Notification', d.message];
return [{json: {query, params}}];
