var raw = $input.item.json;
var payload = raw.body || raw.json || raw;
var tenantId = payload.tenantId || payload.data?.tenantId;
var clientData = payload.data?.client || payload.client || payload.data || {};
var clientId = clientData.id || clientData.clientId;
var planFromPayload = payload.data?.plan || payload.plan || {};
var avecStripe = payload.data?.avecStripe || payload.avecStripe || false;

if (!tenantId) throw new Error('tenantId manquant');
if (!clientId) throw new Error('clientId manquant');

var plan = {
  nomPlan: planFromPayload.nomPlan || 'Plan Starter',
  montantMensuel: planFromPayload.montantMensuel || 29.99,
  modulesInclus: planFromPayload.modulesInclus || ['facturation', 'comptabilite', 'agent_ia'],
  dureeMois: planFromPayload.dureeMois || 1
};

var chars = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*';
var password = '';
for (var i = 0; i < 12; i++) password += chars.charAt(Math.floor(Math.random() * chars.length));

var dateDebut = new Date();
var dateFin = new Date();
dateFin.setMonth(dateFin.getMonth() + plan.dureeMois);

return [{
  json: {
    tenantId: String(tenantId),
    clientId: String(clientId),
    clientData: clientData,
    plan: plan,
    avecStripe: avecStripe,
    plainPassword: password,
    unitAmount: Math.round(plan.montantMensuel * 100),
    dateDebut: dateDebut.toISOString(),
    dateProchainRenouvellement: dateFin.toISOString()
  }
}];

var data = $('02. Parser + Valider').first().json;
var checkoutSession = $('05. Stripe - Checkout Session').first().json;
var customerId = $('04. Stripe - Créer Customer').first().json.id;

return [{
  json: {
    ...data,
    idClientStripe: customerId,
    idSessionCheckoutStripe: checkoutSession.id,
    checkoutUrl: checkoutSession.url,
    statutAbonnement: 'en_attente_paiement'
  }
}];

var data = $('02. Parser + Valider').first().json;

return [{
  json: {
    ...data,
    idClientStripe: null,
    idSessionCheckoutStripe: null,
    checkoutUrl: null,
    statutAbonnement: 'actif'
  }
}];

var d = $input.first().json;
var modules = d.plan.modulesInclus || [];
var modulesLiteral = '{' + modules.map(function(m) { return String(m); }).join(',') + '}';

function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }

var query = 'INSERT INTO client_subscriptions (id, client_final_id, nom_plan, date_debut, date_prochain_renouvellement, montant_mensuel, modules_inclus, statut, id_client_stripe, id_abonnement_stripe, temporary_password, updated_at) VALUES (gen_random_uuid(), ' + esc(d.clientId) + '::uuid, ' + esc(d.plan.nomPlan) + ', ' + esc(d.dateDebut) + '::timestamptz, ' + esc(d.dateProchainRenouvellement) + '::timestamptz, ' + d.plan.montantMensuel + ', ' + esc(modulesLiteral) + '::text[], ' + esc(d.statutAbonnement) + ', ' + (d.idClientStripe ? esc(d.idClientStripe) : 'NULL') + ', ' + (d.idSessionCheckoutStripe ? esc(d.idSessionCheckoutStripe) : 'NULL') + ', ' + esc(d.plainPassword) + ', NOW()) ON CONFLICT (client_final_id) DO UPDATE SET nom_plan = EXCLUDED.nom_plan, montant_mensuel = EXCLUDED.montant_mensuel, modules_inclus = EXCLUDED.modules_inclus, statut = EXCLUDED.statut, id_client_stripe = COALESCE(EXCLUDED.id_client_stripe, client_subscriptions.id_client_stripe), temporary_password = COALESCE(EXCLUDED.temporary_password, client_subscriptions.temporary_password), updated_at = NOW() RETURNING *';

return [{ json: { query: query, allData: d } }];

var d = $('07. Build SQL abonnement').first().json.allData;
var cd = d.clientData || {};
var rawName = cd.raisonSociale || ((cd.nom || '') + ' ' + (cd.prenom || '')).trim() || 'client';
var slug = rawName.toLowerCase().replace(/[^a-z0-9]+/g, '-').replace(/^-|-$/g, '').substring(0, 50);
var tenantSlug = slug + '-' + d.clientId.substring(0, 8);
var folderPath = '/var/www/talosprimes/clients/' + tenantSlug;

var chars = 'abcdefghijklmnopqrstuvwxyz0123456789';
var token = '';
for (var i = 0; i < 32; i++) token += chars.charAt(Math.floor(Math.random() * chars.length));
var dateExpiration = new Date(Date.now() + 30*24*60*60*1000).toISOString();

function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }
var query = 'INSERT INTO client_spaces (id, tenant_id, client_final_id, token, statut, date_expiration, created_at) VALUES (gen_random_uuid(), ' + esc(d.tenantId) + '::uuid, ' + esc(d.clientId) + '::uuid, ' + esc(token) + ', ' + esc('en_attente') + ', ' + esc(dateExpiration) + '::timestamptz, NOW()) ON CONFLICT (client_final_id) DO UPDATE SET token = EXCLUDED.token, date_expiration = EXCLUDED.date_expiration, statut = EXCLUDED.statut RETURNING *';

return [{ json: { query: query, tenantSlug: tenantSlug, folderPath: folderPath, modules: d.plan.modulesInclus || [] } }];

var d = $('07. Build SQL abonnement').first().json.allData;
var spaceData = $('09. Build SQL espace client').first().json;
var spaceRecord = $('10. INSERT client_space').first().json;
var space = spaceRecord[0] || spaceRecord;

var isStripe = d.checkoutUrl ? true : false;

var response = {
  success: true,
  message: isStripe
    ? 'Onboarding initié — redirection vers le paiement Stripe'
    : 'Onboarding terminé — espace client actif',
  client: d.clientData,
  subscription: { statut: d.statutAbonnement, plan: d.plan.nomPlan, montant: d.plan.montantMensuel },
  clientSpace: {
    id: space.id || null,
    tenantSlug: spaceData.tenantSlug,
    statut: isStripe ? 'en_attente' : 'actif',
    modules: spaceData.modules
  },
  stripe: isStripe ? {
    customerId: d.idClientStripe,
    checkoutUrl: d.checkoutUrl,
    requiresPayment: true
  } : null
};

return [{ json: response }];

var d = $('07. Build SQL abonnement').first().json.allData;
var cd = d.clientData || {};
var clientName = cd.prenom || cd.nom || cd.raisonSociale || 'le client';
var stripeInfo = d.checkoutUrl ? ' (Stripe - en attente de paiement)' : ' (sans Stripe - actif)';

function esc(v) { return String(v || '').replace(/'/g, "''"); }

var query = "INSERT INTO notifications (id, tenant_id, titre, message, type, lu, created_at) VALUES (gen_random_uuid(), '" + esc(d.tenantId) + "', 'Nouvel onboarding" + esc(stripeInfo) + "', 'Espace client créé pour " + esc(clientName) + ". Plan: " + esc(d.plan.nomPlan) + " (" + d.plan.montantMensuel + " EUR/mois)" + esc(stripeInfo) + "', 'info', false, NOW())";

return [{ json: { query: query } }];
