// Préparer la réponse de succès
// Récupérer les données depuis le node précédent (10c. Créer Tenant et User)
const credentialsResponse = $input.item.json;
const _body = (credentialsResponse && credentialsResponse.data && typeof credentialsResponse.data === "object") ? { ...credentialsResponse.data, tenantId: credentialsResponse.data.tenantId || credentialsResponse.tenantId } : credentialsResponse;

// Récupérer les données depuis 10a via le contexte du workflow
// Utiliser $node pour accéder aux données du nœud 10a dans le contexte actuel
const prepareData = $node['10a. Préparer après abonnement'].json || {};

// Données client depuis prepareData (qui contient les données de 01)
const clientData = prepareData.clientData || {};

// Vérifier si Stripe est activé
const avecStripe = prepareData.idClientStripe || prepareData.idSessionCheckoutStripe || prepareData.checkoutUrl || prepareData.avecStripe;

// Si Stripe est activé, préparer l'objet stripe
const stripe = avecStripe && prepareData.checkoutUrl ? {
  customerId: prepareData.idClientStripe,
  checkoutSessionId: prepareData.idSessionCheckoutStripe,
  checkoutUrl: prepareData.checkoutUrl,
  requiresPayment: true
} : null;

// Extraire les identifiants depuis la réponse de l'API
const credentialsData = _body.data || credentialsResponse || {};

return {
  json: {
    success: true,
    message: stripe && stripe.checkoutUrl 
      ? "Espace client créé. Redirection vers le paiement..." 
      : "Espace client créé avec succès",
    client: clientData,
    subscription: prepareData.subscriptionData || null,
    modulesActives: prepareData.subscriptionData?.modules_inclus || prepareData.plan?.modulesInclus || [],
    credentials: credentialsData.tenantId ? {
      tenantId: credentialsData.tenantId,
      userId: credentialsData.userId,
      email: credentialsData.email,
      password: prepareData.plainPassword || credentialsData.password || null
    } : null,
    stripe: stripe
  }
};

// Préparer le body JSON pour la notification
// Récupérer les données depuis le nœud précédent (11. Formater réponse)
const responseData = $input.item.json;
const _body = (responseData && responseData.data && typeof responseData.data === "object") ? { ...responseData.data, tenantId: responseData.data.tenantId || responseData.tenantId } : responseData;
const subscription = _body.subscription;
const client = _body.client || {};

// Utiliser les données depuis $node pour accéder à 10a si nécessaire
let tenantId, clientId;
try {
  const prepareData = $node['10a. Préparer après abonnement'].json || {};
  tenantId = prepareData.tenantId;
  clientId = prepareData.clientId;
} catch (e) {
  // Fallback : essayer de récupérer depuis les credentials
  tenantId = _body.credentials?.tenantId;
  clientId = client.id;
}

const clientName = client.prenom || client.nom || client.raisonSociale || 'le client';
const titreStripe = _body.stripe ? ' (Stripe)' : '';
const messageStripe = _body.stripe ? ' - Abonnement Stripe créé' : '';

return {
  json: {
    type: 'client_onboarding',
    titre: `Espace client créé${titreStripe}`,
    message: `L'espace client et l'abonnement ont été créés avec succès pour ${clientName}${messageStripe}`,
    donnees: {
      tenantId: tenantId,
      clientId: clientId,
      subscriptionId: subscription?.id || subscription?.[0]?.id
    }
  }
};
