const raw = $input.first().json;
const payload = (raw && typeof raw === 'object' && 'body' in raw) ? raw.body : raw;
const data = (payload && typeof payload === 'object' && 'data' in payload) ? payload.data : (payload ?? {});

const id = data?.id ? String(data.id) : '';
if (!id) throw new Error('ID requis');

// Extraire les champs optionnels
const updateData = {};
if (data.raisonSociale !== undefined) updateData.raisonSociale = data.raisonSociale;
if (data.nom !== undefined) updateData.nom = data.nom;
if (data.prenom !== undefined) updateData.prenom = data.prenom;
if (data.email !== undefined) updateData.email = data.email;
if (data.telephone !== undefined) updateData.telephone = data.telephone;
if (data.adresse !== undefined) updateData.adresse = data.adresse;
if (data.tags !== undefined) updateData.tags = Array.isArray(data.tags) ? data.tags : [];
if (data.statut !== undefined) updateData.statut = data.statut;

return [{ json: { id, ...updateData } }];
