const raw = $input.first().json;
const payload = (raw && typeof raw === 'object' && 'body' in raw) ? raw.body : raw;
const data = (payload && typeof payload === 'object' && 'data' in payload) ? payload.data : (payload ?? {});
const id = data?.id ? String(data.id) : '';
const tenantId = data?.tenantId ? String(data.tenantId) : '';
if (!id) throw new Error('ID requis');
return [{ json: {
  id,
  tenantId,
  nom: data.nom || '',
  prenom: data.prenom || '',
  email: data.email || '',
  telephone: data.telephone || '',
  type: data.type || '',
  raisonSociale: data.raisonSociale || data.raison_sociale || '',
  adresse: data.adresse || '',
  statut: data.statut || '',
  tags: Array.isArray(data.tags) ? JSON.stringify(data.tags) : ''
}}];
