// Parser le payload avec validation whitelist (anti-injection SQL)
const raw = $input.first().json;
const payload = (raw && typeof raw === 'object' && 'body' in raw) ? raw.body : raw;
const tenantId = String(payload?.tenantId || '').trim();
const data = (payload && typeof payload === 'object' && 'data' in payload) ? payload.data : (payload ?? {});

// Validation UUID du tenantId
const UUID_RE = /^[0-9a-f]{8}-[0-9a-f]{4}-4[0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
if (!tenantId || !UUID_RE.test(tenantId)) throw new Error('Tenant ID requis et doit être un UUID valide');

// Whitelist des valeurs autorisées
const VALID_TYPES = ['particulier', 'professionnel', 'association'];
const VALID_STATUTS = ['actif', 'inactif', 'suspendu', 'archive'];

const rawType = data?.type ? String(data.type).trim().toLowerCase() : null;
const rawStatut = data?.statut ? String(data.statut).trim().toLowerCase() : null;
const rawLimit = data?.limit ? parseInt(String(data.limit), 10) : 100;

const type = rawType && VALID_TYPES.includes(rawType) ? rawType : null;
const statut = rawStatut && VALID_STATUTS.includes(rawStatut) ? rawStatut : null;
const limit = (Number.isFinite(rawLimit) && rawLimit > 0 && rawLimit <= 500) ? rawLimit : 100;

// Construire les clauses WHERE de manière sécurisée
const conditions = [`tenant_id = '${tenantId}'`];
if (type) conditions.push(`type = '${type}'`);
if (statut) conditions.push(`statut = '${statut}'`);
const whereClause = conditions.join(' AND ');

return [{ json: { type, statut, limit, tenantId, whereClause } }];

// Formater la réponse: { clients: [...] }
const items = $input.all();
// Si aucun résultat ou item vide (alwaysOutputData), retourner un tableau vide
if (!items || items.length === 0 || !items[0].json?.id) {
  return [{ json: { clients: [] } }];
}

const clients = items.map(item => {
  const client = item.json;
  return {
    id: client.id,
    tenantId: client.tenant_id,
    type: client.type,
    raisonSociale: client.raison_sociale ?? null,
    nom: client.nom ?? null,
    prenom: client.prenom ?? null,
    email: client.email,
    telephone: client.telephone ?? null,
    adresse: client.adresse ?? null,
    tags: client.tags ?? [],
    statut: client.statut,
    createdAt: client.created_at,
    updatedAt: client.updated_at
  };
});

// Retourner directement { clients } pour que le backend puisse lire result.data?.clients
return [{ json: { clients } }];
