// Parser le payload: { body: { event, tenantId, timestamp, data: { type?, statut?, limit? } } }
const raw = $input.first().json;
const payload = (raw && typeof raw === 'object' && 'body' in raw) ? raw.body : raw;
// Le backend envoie { event, tenantId, timestamp, data: { type, statut, limit } }
const tenantId = String(payload?.tenantId || '').trim();
const data = (payload && typeof payload === 'object' && 'data' in payload) ? payload.data : (payload ?? {});

const type = data?.type ? String(data.type).trim() : null;
const statut = data?.statut ? String(data.statut).trim() : null;
const limit = data?.limit ? parseInt(String(data.limit), 10) : 100;

if (!tenantId) throw new Error('Tenant ID requis');

return [{ json: { type, statut, limit, tenantId } }];

// Formater la réponse: { clients: [...] }
const items = $input.all();
// Si aucun résultat, retourner un tableau vide
if (!items || items.length === 0) {
  return [{ json: { clients: [] } }];
}

const clients = items.map(item => {
  const client = item.json;
  return {
    id: client.id,
    tenantId: client.tenant_id,
    type: client.type,
    raisonSociale: client.raison_sociale ?? null,
    nom: client.nom ?? null,
    prenom: client.prenom ?? null,
    email: client.email,
    telephone: client.telephone ?? null,
    adresse: client.adresse ?? null,
    tags: client.tags ?? [],
    statut: client.statut,
    createdAt: client.created_at,
    updatedAt: client.updated_at
  };
});

// Retourner directement { clients } pour que le backend puisse lire result.data?.clients
return [{ json: { clients } }];
