// Parser le payload avec validation whitelist (anti-injection SQL)
var raw = $input.first().json;
var payload = (raw && typeof raw === 'object' && 'body' in raw) ? raw.body : raw;
var tenantId = String(payload?.tenantId || '').trim();
var data = (payload && typeof payload === 'object' && 'data' in payload) ? payload.data : (payload ?? {});

// Validation UUID du tenantId
var UUID_RE = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!tenantId || !UUID_RE.test(tenantId)) throw new Error('Tenant ID requis et doit être un UUID valide');

// Whitelist des valeurs autorisées
var VALID_TYPES = ['b2b', 'b2c'];
var VALID_STATUTS = ['actif', 'inactif', 'suspendu'];

var rawType = data?.type ? String(data.type).trim().toLowerCase() : null;
var rawStatut = data?.statut ? String(data.statut).trim().toLowerCase() : null;
var rawLimit = data?.limit ? parseInt(String(data.limit), 10) : 100;

var type = rawType && VALID_TYPES.includes(rawType) ? rawType : null;
var statut = rawStatut && VALID_STATUTS.includes(rawStatut) ? rawStatut : null;
var limit = (Number.isFinite(rawLimit) && rawLimit > 0 && rawLimit <= 500) ? rawLimit : 100;

// Construire la requête paramétrisée dynamiquement
var conditions = [];
conditions.push(`tenant_id = '${tenantId}'::uuid`);

if (type) {
  conditions.push(`type = '${type}'`);
}
if (statut) {
  conditions.push(`statut = '${statut}'`);
}

var whereClause = conditions.join(' AND ');
var query = `SELECT id, tenant_id, type, raison_sociale, nom, prenom, email, telephone, adresse, tags, statut, created_at, updated_at
FROM client_finals
WHERE ${whereClause}
ORDER BY created_at DESC
LIMIT ${limit}`;

return [{ json: { query } }];

// Formater la réponse: { clients: [...] }
var items = $input.all();
// Si aucun résultat ou item vide (alwaysOutputData), retourner un tableau vide
if (!items || items.length === 0 || !items[0].json?.id) {
  return [{ json: { clients: [] } }];
}

var clients = items.map(function(item) { return {
  var client = item.json;
  return {
    id: client.id,
    tenantId: client.tenant_id,
    type: client.type,
    raisonSociale: client.raison_sociale ?? null,
    nom: client.nom ?? null,
    prenom: client.prenom ?? null,
    email: client.email,
    telephone: client.telephone ?? null,
    adresse: client.adresse ?? null,
    tags: client.tags ?? [],
    statut: client.statut,
    createdAt: client.created_at,
    updatedAt: client.updated_at
  };
}; });

// Retourner directement { clients } pour que le backend puisse lire result.data?.clients
return [{ json: { clients } }];
