parameters.jsCode:
var raw = $input.item.json;
// Stripe envoie: { body: { id, type, data: { object: { ... } } } }
var body = raw.body || raw;
var event = body;
var session = event.data?.object || event;

// Log pour debug
var eventType = event.type || 'unknown';
if (eventType !== 'checkout.session.completed' && eventType !== 'unknown') {
  throw new Error('Event type inattendu: ' + eventType + ' (attendu: checkout.session.completed)');
}

var customerId = session.customer;
var subscriptionId = session.subscription;
var paymentStatus = session.payment_status;
var clientId = session.metadata?.clientId;
var tenantId = session.metadata?.tenantId;
var planName = session.metadata?.planName;
var amountTotal = session.amount_total;
var currency = session.currency;
var customerEmail = session.customer_details?.email || session.customer_email;

if (!clientId || !tenantId) {
  throw new Error('Metadata manquantes: clientId=' + (clientId || 'ABSENT') + ', tenantId=' + (tenantId || 'ABSENT') + '. Raw keys: ' + Object.keys(session).join(','));
}

return [{
  json: {
    customerId,
    subscriptionId,
    paymentStatus,
    clientId,
    tenantId,
    planName,
    amountTotal,
    currency,
    customerEmail,
    sessionId: session.id
  }
}];

var credResult = $('06. Récupérer identifiants').first().json;
var stripeData = $('02. Parser données session').first().json;
var record = credResult[0] || credResult;

var email = record.email || stripeData.customerEmail;
var password = record.temporary_password;
var nom = record.prenom ? (record.prenom + ' ' + (record.nom || '')) : (record.raison_sociale || 'Client');
var loginUrl = 'https://talosprimes.com/login';

if (!email || !password) {
  throw new Error('Email ou mot de passe manquant: email=' + (email || 'ABSENT') + ', password=' + (password ? 'OK' : 'ABSENT'));
}

var htmlTemplate = '<!DOCTYPE html>' +
'<html><head><meta charset="UTF-8"><style>' +
'body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }' +
'.container { max-width: 600px; margin: 0 auto; padding: 20px; }' +
'.header { background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 30px; text-align: center; border-radius: 10px 10px 0 0; }' +
'.content { background: #f9f9f9; padding: 30px; border-radius: 0 0 10px 10px; }' +
'.credentials { background: white; padding: 20px; margin: 20px 0; border-radius: 8px; border-left: 4px solid #667eea; }' +
'.credential-label { font-weight: bold; color: #667eea; }' +
'.credential-value { font-family: monospace; background: #f0f0f0; padding: 8px; border-radius: 4px; display: inline-block; margin-top: 5px; }' +
'.button { display: inline-block; padding: 12px 30px; background: #667eea; color: white; text-decoration: none; border-radius: 5px; margin: 20px 0; }' +
'.warning { background: #fff3cd; border-left: 4px solid #ffc107; padding: 15px; margin: 20px 0; border-radius: 4px; }' +
'.footer { text-align: center; margin-top: 30px; color: #666; font-size: 12px; }' +
'</style></head><body><div class="container">' +
'<div class="header"><h1>Bienvenue sur TalosPrimes !</h1><p>Votre paiement a ete valide avec succes</p></div>' +
'<div class="content">' +
'<p>Bonjour ' + nom.trim() + ',</p>' +
'<p>Votre abonnement TalosPrimes a ete active avec succes. Vous pouvez maintenant acceder a votre espace client.</p>' +
'<div class="credentials">' +
'<h2 style="margin-top: 0;">Vos identifiants de connexion :</h2>' +
'<div style="margin: 10px 0;"><div class="credential-label">Email :</div><div class="credential-value">' + email + '</div></div>' +
'<div style="margin: 10px 0;"><div class="credential-label">Mot de passe temporaire :</div><div class="credential-value">' + password + '</div></div>' +
'</div>' +
'<div class="warning"><strong>Important :</strong> Pour des raisons de securite, vous devrez changer votre mot de passe lors de votre premiere connexion.</div>' +
'<p style="text-align: center;"><a href="' + loginUrl + '" class="button">Se connecter maintenant</a></p>' +
'<p>Cordialement,<br>L\'equipe TalosPrimes</p>' +
'</div><div class="footer"><p>Cet email a ete envoye automatiquement.</p></div></div></body></html>';

return [{ json: {
  to: email,
  subject: 'Bienvenue sur TalosPrimes - Vos identifiants de connexion',
  html: htmlTemplate
} }];

var data = $('02. Parser données session').item.json;
var montant = data.amountTotal ? (data.amountTotal / 100).toFixed(2) + ' ' + (data.currency || 'EUR').toUpperCase() : 'N/A';
var planName = (data.planName || 'Abonnement').replace(/'/g, "''");
var clientInfo = (data.customerEmail || data.clientId).replace(/'/g, "''");

return [{
  json: {
    query: "INSERT INTO notifications (id, tenant_id, titre, message, type, lu, created_at) VALUES (gen_random_uuid(), '" + data.tenantId.replace(/'/g, "''") + "', 'Paiement confirme - " + planName + "', 'Le paiement de " + montant + " a ete confirme pour " + clientInfo + ". Abonnement et espace client actifs.', 'success', false, NOW())"
  }
}];
