// Extraire les données du webhook Stripe
const payload = $input.item.json;
const _body = (payload && payload.data && typeof payload.data === "object") ? { ...payload.data, tenantId: payload.data.tenantId || payload.tenantId } : payload;
const session = _body.body?.data?.object || _body.body?.object || payload;

// Vérifier que c'est un événement checkout.session.completed
const eventType = _body.body?.type || _body.type;
if (eventType !== 'checkout.session.completed') {
  return {
    json: { skip: true, reason: 'Event type not checkout.session.completed' }
  };
}

// Extraire les métadonnées de la session
const metadata = session.metadata || {};
const clientId = metadata.clientId;
const tenantId = metadata.tenantId;

// Extraire les informations de l'abonnement créé
const subscriptionId = session.subscription;

return {
  json: {
    eventType,
    sessionId: session.id,
    clientId,
    tenantId,
    subscriptionId,
    customerId: session.customer,
    customerEmail: session.customer_details?.email || session.customer_email,
    amountTotal: session.amount_total / 100, // Convertir de centimes en euros
    currency: session.currency
  }
};

// Preparer l'email avec les identifiants
const credentialsData = $input.item.json;
const _body = (credentialsData && credentialsData.data && typeof credentialsData.data === "object") ? { ...credentialsData.data, tenantId: credentialsData.data.tenantId || credentialsData.tenantId } : credentialsData;

// Récupérer les données Stripe depuis le nœud 02
let stripeData = {};
try {
  stripeData = $node['02. Extraire données'].json || {};
} catch (e) {
  // Fallback si $node ne fonctionne pas
  stripeData = {};
}

const email = stripeData.customerEmail || _body.data?.email;
const password = _body.data?.password;

if (!email || !password) {
  throw new Error('Email ou mot de passe manquant');
}

// URL de connexion
const loginUrl = 'https://talosprimes.com/login';

// Template HTML pour l'email
const htmlTemplate = '<!DOCTYPE html>\n' +
'<html>\n' +
'<head>\n' +
'  <meta charset=\"UTF-8\">\n' +
'  <style>\n' +
'    body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }\n' +
'    .container { max-width: 600px; margin: 0 auto; padding: 20px; }\n' +
'    .header { background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 30px; text-align: center; border-radius: 10px 10px 0 0; }\n' +
'    .content { background: #f9f9f9; padding: 30px; border-radius: 0 0 10px 10px; }\n' +
'    .credentials { background: white; padding: 20px; margin: 20px 0; border-radius: 8px; border-left: 4px solid #667eea; }\n' +
'    .credential-item { margin: 10px 0; }\n' +
'    .credential-label { font-weight: bold; color: #667eea; }\n' +
'    .credential-value { font-family: monospace; background: #f0f0f0; padding: 8px; border-radius: 4px; display: inline-block; margin-top: 5px; }\n' +
'    .button { display: inline-block; padding: 12px 30px; background: #667eea; color: white; text-decoration: none; border-radius: 5px; margin: 20px 0; }\n' +
'    .warning { background: #fff3cd; border-left: 4px solid #ffc107; padding: 15px; margin: 20px 0; border-radius: 4px; }\n' +
'    .footer { text-align: center; margin-top: 30px; color: #666; font-size: 12px; }\n' +
'  </style>\n' +
'</head>\n' +
'<body>\n' +
'  <div class=\"container\">\n' +
'    <div class=\"header\">\n' +
'      <h1>Bienvenue sur TalosPrimes !</h1>\n' +
'      <p>Votre paiement a ete valide avec succes</p>\n' +
'    </div>\n' +
'    <div class=\"content\">\n' +
'      <p>Bonjour,</p>\n' +
'      <p>Votre abonnement TalosPrimes a ete active avec succes. Vous pouvez maintenant acceder a votre espace client.</p>\n' +
'      <div class=\"credentials\">\n' +
'        <h2 style=\"margin-top: 0;\">Vos identifiants de connexion :</h2>\n' +
'        <div class=\"credential-item\">\n' +
'          <div class=\"credential-label\">Email :</div>\n' +
'          <div class=\"credential-value\">' + email + '</div>\n' +
'        </div>\n' +
'        <div class=\"credential-item\">\n' +
'          <div class=\"credential-label\">Mot de passe temporaire :</div>\n' +
'          <div class=\"credential-value\">' + password + '</div>\n' +
'        </div>\n' +
'      </div>\n' +
'      <div class=\"warning\">\n' +
'        <strong>Important :</strong> Pour des raisons de securite, vous devrez changer votre mot de passe lors de votre premiere connexion.\n' +
'      </div>\n' +
'      <p style=\"text-align: center;\">\n' +
'        <a href=\"' + loginUrl + '\" class=\"button\">Se connecter maintenant</a>\n' +
'      </p>\n' +
'      <p>Si vous avez des questions, n\'hesitez pas a nous contacter.</p>\n' +
'      <p>Cordialement,<br>L\'equipe TalosPrimes</p>\n' +
'    </div>\n' +
'    <div class=\"footer\">\n' +
'      <p>Cet email a ete envoye automatiquement. Ne repondez pas a cet email.</p>\n' +
'    </div>\n' +
'  </div>\n' +
'</body>\n' +
'</html>';

return {
  json: {
    to: email,
    subject: 'Bienvenue sur TalosPrimes - Vos identifiants de connexion',
    html: htmlTemplate
  }
};
