const body = $input.first().json.body || $input.first().json;
if (!body.tenantId) throw new Error('tenantId is required');
if (!body.avoirId) throw new Error('avoirId is required');
return [{ json: { tenantId: body.tenantId, avoirId: body.avoirId } }];

const avoirArr = $('05. Get Avoir').all().map(i => i.json).filter(a => a.id);
if (avoirArr.length === 0) throw new Error('Avoir non trouvé');
const avoir = avoirArr[0];

const exerciceArr = $('06. Get Exercice').all().map(i => i.json).filter(e => e.id);
if (exerciceArr.length === 0) throw new Error('Aucun exercice ouvert');

const exerciceId = exerciceArr[0].id;
const nextNum = parseInt($('07. Next Number').first().json.next_num) || 1;
const year = new Date().getFullYear();
const numeroPiece = `EC-${year}-${String(nextNum).padStart(6, '0')}`;

const montantHt = parseFloat(avoir.montant_ht) || 0;
const montantTtc = parseFloat(avoir.montant_ttc) || 0;
const tva = Math.round((montantTtc - montantHt) * 100) / 100;
const clientName = avoir.client_raison_sociale || avoir.client_nom || 'Client';
const dateEcriture = avoir.date_avoir || new Date().toISOString().split('T')[0];

return [{ json: {
  tenantId: avoir.tenant_id, exerciceId, numeroPiece, dateEcriture,
  libelle: `Avoir ${avoir.numero_avoir} - ${clientName}`,
  referenceDoc: avoir.numero_avoir,
  avoirId: avoir.id,
  montantHt, montantTtc, tva
} }];

const ecriture = $('09. Insert Ecriture').first().json;
const lignes = $('10. Insert Lines').all().map(i => i.json).filter(l => l.id);
const b = $('08. Build Ecriture').first().json;

return [{ json: {
  success: true,
  message: `Écriture avoir ${ecriture.numero_piece} créée (contrepassation)`,
  ecriture: {
    id: ecriture.id, numeroPiece: ecriture.numero_piece, journal: 'VE',
    montantTtc: b.montantTtc, montantHt: b.montantHt, tva: b.tva,
    lignes: lignes.map(l => ({ numeroCompte: l.numero_compte, debit: parseFloat(l.debit), credit: parseFloat(l.credit) }))
  }
} }];
