parameters.jsCode:
var body = $input.first().json.body || $input.first().json;
if (!body.tenantId) throw new Error('tenantId is required');
if (!body.invoiceId) throw new Error('invoiceId is required');
return [{ json: { tenantId: body.tenantId, invoiceId: body.invoiceId } }];

function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }
var tenantId = $json.tenantId;
var invoiceId = $json.invoiceId;
if (!tenantId || !invoiceId) throw new Error('Données manquantes (tenantId ou invoiceId)');
var q = 'SELECT i.id, i.tenant_id, i.type, i.numero_facture, i.date_facture, i.montant_ht, i.montant_ttc, i.tva_taux, i.statut, cf.nom as client_nom, cf.raison_sociale as client_raison_sociale FROM invoices i LEFT JOIN client_finals cf ON i.client_final_id = cf.id WHERE i.id = ' + esc(invoiceId) + '::uuid AND i.tenant_id = ' + esc(tenantId) + '::uuid';
return [{ json: { tenantId: tenantId, invoiceId: invoiceId, query: q } }];

function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }
var tenantId = $('02. Parser').first().json.tenantId;
var invoiceId = $('02. Parser').first().json.invoiceId;

// Vérifier que la facture a été trouvée
var invRows = $('06. Get Invoice').all().map(function(i) { return i.json; }).filter(function(i) { return i.id; });
if (invRows.length === 0) throw new Error('Facture introuvable id=' + invoiceId);
var invoice = invRows[0];

// Vérifier si déjà comptabilisé
var checkQuery = 'SELECT id FROM ecritures_comptables WHERE source_auto = ' + esc('facture') + ' AND source_id = ' + esc(invoice.id) + '::uuid AND tenant_id = ' + esc(tenantId) + '::uuid LIMIT 1';

// Chercher exercice ouvert
var exerciceQuery = 'SELECT id FROM exercices_comptables WHERE tenant_id = ' + esc(tenantId) + '::uuid AND cloture = false ORDER BY date_debut DESC LIMIT 1';

return [{ json: { tenantId: tenantId, invoiceId: invoiceId, invoice: invoice, checkQuery: checkQuery, exerciceQuery: exerciceQuery } }];

var rows = $input.all().map(function(i) { return i.json; }).filter(function(r) { return r.id; });
var prev = $('07. Validate Invoice').first().json;
if (rows.length > 0) {
  return [{ json: { alreadyExists: true } }];
}
return [{ json: { alreadyExists: false, tenantId: prev.tenantId, invoiceId: prev.invoiceId, invoice: prev.invoice, exerciceQuery: prev.exerciceQuery } }];

function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }
function num(v) { var n = parseFloat(v); return isNaN(n) ? '0' : String(Math.round(n * 100) / 100); }

var tenantId = $('02. Parser').first().json.tenantId;
var invoice = $('09. Already Exists?').first().json.invoice;

// Exercice ouvert obligatoire
var exerciceArr = $('10. Get Exercice').all().map(function(i) { return i.json; }).filter(function(e) { return e.id; });
if (exerciceArr.length === 0) throw new Error('Aucun exercice comptable ouvert pour tenant ' + tenantId);
var exerciceId = exerciceArr[0].id;

// Prochain numéro de pièce
var nextNumQuery = "SELECT COALESCE(MAX(CAST(SUBSTRING(numero_piece FROM 'EC-[0-9]{4}-([0-9]+)') AS INTEGER)), 0) + 1 as next_num FROM ecritures_comptables WHERE tenant_id = " + esc(tenantId) + "::uuid AND numero_piece LIKE 'EC-' || TO_CHAR(CURRENT_DATE, 'YYYY') || '-%'";

return [{ json: { tenantId: tenantId, invoice: invoice, exerciceId: exerciceId, nextNumQuery: nextNumQuery } }];

function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }
function num(v) { var n = parseFloat(v); return isNaN(n) ? '0' : String(Math.round(n * 100) / 100); }

var prev = $('11. Validate Exercice').first().json;
var invoice = prev.invoice;
var tenantId = prev.tenantId;
var exerciceId = prev.exerciceId;

var nextNum = parseInt($('12. Get Next Num').first().json.next_num) || 1;
var year = new Date().getFullYear();
var numeroPiece = 'EC-' + year + '-' + String(nextNum).padStart(6, '0');

var montantHt = parseFloat(invoice.montant_ht) || 0;
var montantTtc = parseFloat(invoice.montant_ttc) || 0;
var tva = Math.round((montantTtc - montantHt) * 100) / 100;
var isAchat = invoice.type === 'facture_achat';
var clientName = invoice.client_raison_sociale || invoice.client_nom || (isAchat ? 'Fournisseur' : 'Client');
var dateEcriture = invoice.date_facture ? String(invoice.date_facture).split('T')[0] : new Date().toISOString().split('T')[0];
var journalCode = isAchat ? 'AC' : 'VE';
var libelle = (isAchat ? 'Achat ' : 'Facture ') + (invoice.numero_facture || '') + ' - ' + clientName;
var referenceDoc = invoice.numero_facture || '';

var insertQuery = 'INSERT INTO ecritures_comptables (id, tenant_id, exercice_id, journal_code, numero_piece, date_ecriture, libelle, reference_doc, source_auto, source_id, statut, updated_at) VALUES (gen_random_uuid(), ' + esc(tenantId) + '::uuid, ' + esc(exerciceId) + '::uuid, ' + esc(journalCode) + ', ' + esc(numeroPiece) + ', ' + esc(dateEcriture) + '::date, ' + esc(libelle) + ', ' + esc(referenceDoc) + ', ' + esc('facture') + ', ' + esc(invoice.id) + '::uuid, ' + esc('validee') + ', NOW()) RETURNING id, numero_piece';

return [{ json: { tenantId: tenantId, exerciceId: exerciceId, numeroPiece: numeroPiece, montantHt: montantHt, montantTtc: montantTtc, tva: tva, isAchat: isAchat, invoiceType: invoice.type, journalCode: journalCode, referenceDoc: referenceDoc, invoiceId: invoice.id, insertQuery: insertQuery } }];

function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }
function num(v) { var n = parseFloat(v); return isNaN(n) ? '0' : String(Math.round(n * 100) / 100); }

var ecritureId = $('14. Insert Ecriture').first().json.id;
var b = $('13. Build Insert').first().json;

if (!ecritureId) throw new Error('INSERT ecriture_comptable a échoué - pas d id retourné');

var q;
if (b.isAchat) {
  // Facture d achat : Debit charge + TVA deductible, Credit fournisseur
  q = 'INSERT INTO lignes_ecritures (id, ecriture_id, numero_compte, libelle_ligne, debit, credit, ordre) VALUES ' +
    '(gen_random_uuid(), ' + esc(ecritureId) + '::uuid, ' + esc('607000') + ', ' + esc('Achat HT - ' + b.referenceDoc) + ', ' + num(b.montantHt) + ', 0, 0), ' +
    '(gen_random_uuid(), ' + esc(ecritureId) + '::uuid, ' + esc('445660') + ', ' + esc('TVA déductible - ' + b.referenceDoc) + ', ' + num(b.tva) + ', 0, 1), ' +
    '(gen_random_uuid(), ' + esc(ecritureId) + '::uuid, ' + esc('401000') + ', ' + esc('Fournisseur - ' + b.referenceDoc) + ', 0, ' + num(b.montantTtc) + ', 2) ' +
    'RETURNING id, numero_compte, debit, credit';
} else {
  // Facture de vente : Debit client, Credit produit + TVA collectee
  var compteProduit = b.invoiceType === 'facture_entreprise' ? '706000' : '707000';
  q = 'INSERT INTO lignes_ecritures (id, ecriture_id, numero_compte, libelle_ligne, debit, credit, ordre) VALUES ' +
    '(gen_random_uuid(), ' + esc(ecritureId) + '::uuid, ' + esc('411000') + ', ' + esc('Client - ' + b.referenceDoc) + ', ' + num(b.montantTtc) + ', 0, 0), ' +
    '(gen_random_uuid(), ' + esc(ecritureId) + '::uuid, ' + esc(compteProduit) + ', ' + esc('Produit HT - ' + b.referenceDoc) + ', 0, ' + num(b.montantHt) + ', 1), ' +
    '(gen_random_uuid(), ' + esc(ecritureId) + '::uuid, ' + esc('445710') + ', ' + esc('TVA collectée - ' + b.referenceDoc) + ', 0, ' + num(b.tva) + ', 2) ' +
    'RETURNING id, numero_compte, debit, credit';
}

return [{ json: { ecritureId: ecritureId, numeroPiece: b.numeroPiece, montantTtc: b.montantTtc, montantHt: b.montantHt, tva: b.tva, isAchat: b.isAchat, insertLignesQuery: q } }];

var b = $('15. Build Lines').first().json;
var lignes = $('16. Insert Lines').all().map(function(i) { return i.json; }).filter(function(l) { return l.id; });
var journal = $('13. Build Insert').first().json.journalCode || 'VE';
var typeLabel = b.isAchat ? 'achat' : 'vente';

return [{ json: {
  success: true,
  message: 'Écriture comptable ' + typeLabel + ' ' + b.numeroPiece + ' créée automatiquement',
  ecriture: {
    id: b.ecritureId,
    numeroPiece: b.numeroPiece,
    journal: journal,
    type: typeLabel,
    montantTtc: b.montantTtc,
    montantHt: b.montantHt,
    tva: b.tva,
    lignes: lignes.map(function(l) { return { numeroCompte: l.numero_compte, debit: parseFloat(l.debit), credit: parseFloat(l.credit) }; })
  }
} }];
