var body = $input.first().json.body || $input.first().json;
if (!body.tenantId) throw new Error('tenantId is required');
if (!body.invoiceId) throw new Error('invoiceId is required');
return [{ json: { tenantId: body.tenantId, invoiceId: body.invoiceId } }];

var inv = $('05. Get Invoice').all().map(function(i) { return i.json; }).filter(function(i) { return i.id; });
if (inv.length === 0) throw new Error('Facture non trouvée');
var invoice = inv[0];

var exerciceArr = $('07. Get Exercice').all().map(function(i) { return i.json; }).filter(function(e) { return e.id; });
if (exerciceArr.length === 0) throw new Error('Aucun exercice ouvert');

var exerciceId = exerciceArr[0].id;
var nextNum = parseInt($('08. Next Number').first().json.next_num) || 1;
var year = new Date().getFullYear();
var numeroPiece = `EC-${year}-${String(nextNum).padStart(6, '0')}`;

var montantHt = parseFloat(invoice.montant_ht) || 0;
var montantTtc = parseFloat(invoice.montant_ttc) || 0;
var tva = Math.round((montantTtc - montantHt) * 100) / 100;
var clientName = invoice.client_raison_sociale || invoice.client_nom || 'Client';
var dateEcriture = invoice.date_facture || new Date().toISOString().split('T')[0];

var compteProduit = invoice.type === 'facture_entreprise' ? '706000' : '707000';

return [{ json: {
  tenantId: invoice.tenant_id,
  exerciceId,
  numeroPiece,
  dateEcriture,
  libelle: `Facture ${invoice.numero_facture} - ${clientName}`,
  referenceDoc: invoice.numero_facture,
  invoiceId: invoice.id,
  montantHt,
  montantTtc,
  tva,
  compteProduit
} }];

var ecriture = $('10. Insert Ecriture').first().json;
var lignes = $('11. Insert Lines').all().map(function(i) { return i.json; }).filter(function(l) { return l.id; });
var b = $('09. Build Ecriture').first().json;

return [{ json: {
  success: true,
  message: `Écriture comptable ${ecriture.numero_piece} créée automatiquement`,
  ecriture: {
    id: ecriture.id,
    numeroPiece: ecriture.numero_piece,
    journal: 'VE',
    montantTtc: b.montantTtc,
    montantHt: b.montantHt,
    tva: b.tva,
    lignes: lignes.map(function(l) { return { numeroCompte: l.numero_compte, debit: parseFloat(l.debit), credit: parseFloat(l.credit) }; })
  }
} }];
