parameters.jsCode:
var body = $input.first().json.body || $input.first().json;
if (!body.tenantId) throw new Error('tenantId is required');
if (!body.invoiceId) throw new Error('invoiceId is required');
return [{ json: { tenantId: body.tenantId, invoiceId: body.invoiceId } }];

function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }
function num(v) { var n = parseFloat(v); return isNaN(n) ? '0' : String(n); }

var tenantId = $('02. Parser').first().json.tenantId;
var invoiceId = $('02. Parser').first().json.invoiceId;

var getInvoiceQuery = 'SELECT i.*, cf.nom as client_nom, cf.raison_sociale as client_raison_sociale FROM invoices i LEFT JOIN client_finals cf ON i.client_final_id = cf.id WHERE i.id = ' + esc(invoiceId) + '::uuid AND i.tenant_id = ' + esc(tenantId) + '::uuid';

var getExerciceQuery = 'SELECT id FROM exercices_comptables WHERE tenant_id = ' + esc(tenantId) + '::uuid AND cloture = false ORDER BY date_debut DESC LIMIT 1';

var getNextNumQuery = "SELECT COALESCE(MAX(CAST(SUBSTRING(numero_piece FROM 'EC-[0-9]{4}-([0-9]+)') AS INTEGER)), 0) + 1 as next_num FROM ecritures_comptables WHERE tenant_id = " + esc(tenantId) + "::uuid AND numero_piece LIKE 'EC-' || TO_CHAR(CURRENT_DATE, 'YYYY') || '-%'";

return [{ json: { tenantId: tenantId, invoiceId: invoiceId, getInvoiceQuery: getInvoiceQuery, getExerciceQuery: getExerciceQuery, getNextNumQuery: getNextNumQuery } }];

function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }
function num(v) { var n = parseFloat(v); return isNaN(n) ? '0' : String(n); }

var invRows = $('05. Get Invoice').all().map(function(i) { return i.json; }).filter(function(i) { return i.id; });
if (invRows.length === 0) throw new Error('Facture non trouvée pour id=' + $('02. Parser').first().json.invoiceId);
var invoice = invRows[0];

var exerciceArr = $('06. Get Exercice').all().map(function(i) { return i.json; }).filter(function(e) { return e.id; });
if (exerciceArr.length === 0) throw new Error('Aucun exercice comptable ouvert pour tenant=' + invoice.tenant_id + '. Lancez le seed ou créez un exercice.');
var exerciceId = exerciceArr[0].id;

var nextNum = parseInt($('07. Get Next Num').first().json.next_num) || 1;
var year = new Date().getFullYear();
var numeroPiece = 'EC-' + year + '-' + String(nextNum).padStart(6, '0');

var montantHt = parseFloat(invoice.montant_ht) || 0;
var montantTtc = parseFloat(invoice.montant_ttc) || 0;
var tva = Math.round((montantTtc - montantHt) * 100) / 100;
var clientName = invoice.client_raison_sociale || invoice.client_nom || 'Client';
var dateEcriture = invoice.date_facture ? String(invoice.date_facture).split('T')[0] : new Date().toISOString().split('T')[0];
var compteProduit = invoice.type === 'facture_entreprise' ? '706000' : '707000';
var libelle = 'Facture ' + (invoice.numero_facture || '') + ' - ' + clientName;
var referenceDoc = invoice.numero_facture || '';

// Vérifier si déjà comptabilisé
var checkQuery = 'SELECT id FROM ecritures_comptables WHERE source_auto = ' + esc('facture') + ' AND source_id = ' + esc(invoice.id) + '::uuid AND tenant_id = ' + esc(invoice.tenant_id) + '::uuid LIMIT 1';

var insertEcritureQuery = 'INSERT INTO ecritures_comptables (id, tenant_id, exercice_id, journal_code, numero_piece, date_ecriture, libelle, reference_doc, source_auto, source_id, statut) VALUES (gen_random_uuid(), ' + esc(invoice.tenant_id) + '::uuid, ' + esc(exerciceId) + '::uuid, ' + esc('VE') + ', ' + esc(numeroPiece) + ', ' + esc(dateEcriture) + '::date, ' + esc(libelle) + ', ' + esc(referenceDoc) + ', ' + esc('facture') + ', ' + esc(invoice.id) + '::uuid, ' + esc('validee') + ') RETURNING id, numero_piece';

return [{ json: { tenantId: invoice.tenant_id, exerciceId: exerciceId, numeroPiece: numeroPiece, montantHt: montantHt, montantTtc: montantTtc, tva: tva, compteProduit: compteProduit, referenceDoc: referenceDoc, invoiceId: invoice.id, checkQuery: checkQuery, insertEcritureQuery: insertEcritureQuery } }];

function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }
function num(v) { var n = parseFloat(v); return isNaN(n) ? '0' : String(n); }

var ecritureId = $('10. Insert Ecriture').first().json.id;
var b = $('08. Build Ecriture').first().json;

if (!ecritureId) throw new Error('Échec INSERT écriture comptable - pas d\'id retourné');

var insertLignesQuery = 'INSERT INTO lignes_ecritures (id, ecriture_id, numero_compte, libelle_ligne, debit, credit, ordre) VALUES ' +
  '(gen_random_uuid(), ' + esc(ecritureId) + '::uuid, ' + esc('411000') + ', ' + esc('Client - ' + b.referenceDoc) + ', ' + num(b.montantTtc) + ', 0, 0), ' +
  '(gen_random_uuid(), ' + esc(ecritureId) + '::uuid, ' + esc(b.compteProduit) + ', ' + esc('Produit HT - ' + b.referenceDoc) + ', 0, ' + num(b.montantHt) + ', 1), ' +
  '(gen_random_uuid(), ' + esc(ecritureId) + '::uuid, ' + esc('445710') + ', ' + esc('TVA collectée - ' + b.referenceDoc) + ', 0, ' + num(b.tva) + ', 2) ' +
  'RETURNING id, numero_compte, debit, credit';

return [{ json: { ecritureId: ecritureId, numeroPiece: b.numeroPiece, insertLignesQuery: insertLignesQuery, montantTtc: b.montantTtc, montantHt: b.montantHt, tva: b.tva } }];

var b = $('11. Build Lines Query').first().json;
var lignes = $('12. Insert Lines').all().map(function(i) { return i.json; }).filter(function(l) { return l.id; });

return [{ json: {
  success: true,
  message: 'Écriture comptable ' + b.numeroPiece + ' créée automatiquement',
  ecriture: {
    id: b.ecritureId,
    numeroPiece: b.numeroPiece,
    journal: 'VE',
    montantTtc: b.montantTtc,
    montantHt: b.montantHt,
    tva: b.tva,
    lignes: lignes.map(function(l) { return { numeroCompte: l.numero_compte, debit: parseFloat(l.debit), credit: parseFloat(l.credit) }; })
  }
} }];
