const body = $input.first().json.body || $input.first().json;
if (!body.tenantId) throw new Error('tenantId is required');
if (!body.invoiceId) throw new Error('invoiceId is required');
return [{ json: { tenantId: body.tenantId, invoiceId: body.invoiceId } }];

const inv = $('05. Get Invoice').all().map(i => i.json).filter(i => i.id);
if (inv.length === 0) throw new Error('Facture non trouvée');
const invoice = inv[0];

const exerciceArr = $('07. Get Exercice').all().map(i => i.json).filter(e => e.id);
if (exerciceArr.length === 0) throw new Error('Aucun exercice ouvert');

const exerciceId = exerciceArr[0].id;
const nextNum = parseInt($('08. Next Number').first().json.next_num) || 1;
const year = new Date().getFullYear();
const numeroPiece = `EC-${year}-${String(nextNum).padStart(6, '0')}`;

const montantHt = parseFloat(invoice.montant_ht) || 0;
const montantTtc = parseFloat(invoice.montant_ttc) || 0;
const tva = Math.round((montantTtc - montantHt) * 100) / 100;
const clientName = invoice.client_raison_sociale || invoice.client_nom || 'Client';
const dateEcriture = invoice.date_facture || new Date().toISOString().split('T')[0];

const compteProduit = invoice.type === 'facture_entreprise' ? '706000' : '707000';

return [{ json: {
  tenantId: invoice.tenant_id,
  exerciceId,
  numeroPiece,
  dateEcriture,
  libelle: `Facture ${invoice.numero_facture} - ${clientName}`,
  referenceDoc: invoice.numero_facture,
  invoiceId: invoice.id,
  montantHt,
  montantTtc,
  tva,
  compteProduit
} }];

const ecriture = $('10. Insert Ecriture').first().json;
const lignes = $('11. Insert Lines').all().map(i => i.json).filter(l => l.id);
const b = $('09. Build Ecriture').first().json;

return [{ json: {
  success: true,
  message: `Écriture comptable ${ecriture.numero_piece} créée automatiquement`,
  ecriture: {
    id: ecriture.id,
    numeroPiece: ecriture.numero_piece,
    journal: 'VE',
    montantTtc: b.montantTtc,
    montantHt: b.montantHt,
    tva: b.tva,
    lignes: lignes.map(l => ({ numeroCompte: l.numero_compte, debit: parseFloat(l.debit), credit: parseFloat(l.credit) }))
  }
} }];
