var body = $input.first().json.body || $input.first().json;
if (!body.tenantId) throw new Error('tenantId is required');
var UUID_RE = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!UUID_RE.test(body.tenantId)) throw new Error('tenantId invalide');
var today = new Date().toISOString().split('T')[0];
return [{ json: { tenantId: body.tenantId, dateFrom: body.dateFrom || null, dateTo: body.dateTo || today } }];

function esc(v) { return "'" + String(v).replace(/'/g, "''") + "'"; }
var p = $('Parser').first().json;
var dateFilter = '';
if (p.dateFrom) dateFilter += ' AND e.date_ecriture >= ' + esc(p.dateFrom) + '::date';
if (p.dateTo) dateFilter += ' AND e.date_ecriture <= ' + esc(p.dateTo) + '::date';
var query = "SELECT le.numero_compte, pc.libelle as compte_libelle, pc.classe, COALESCE(SUM(le.debit), 0) as total_debit, COALESCE(SUM(le.credit), 0) as total_credit FROM lignes_ecritures le JOIN ecritures_comptables e ON le.ecriture_id = e.id LEFT JOIN plan_comptable pc ON le.numero_compte = pc.numero_compte AND pc.tenant_id = e.tenant_id WHERE e.tenant_id = " + esc(p.tenantId) + "::uuid AND e.statut = 'validee'" + dateFilter + " GROUP BY le.numero_compte, pc.libelle, pc.classe HAVING COALESCE(SUM(le.debit), 0) != 0 OR COALESCE(SUM(le.credit), 0) != 0 ORDER BY le.numero_compte ASC";
return [{ json: { query: query } }];

var classLabels = { 1: 'Comptes de capitaux', 2: "Comptes d'immobilisations", 3: 'Comptes de stocks', 4: 'Comptes de tiers', 5: 'Comptes financiers', 6: 'Comptes de charges', 7: 'Comptes de produits' };
var items = $('Query Balance').all().map(function(i) { return i.json; }).filter(function(r) { return r.numero_compte; });
var grouped = {};
items.forEach(function(row) {
  var classe = row.classe || 0;
  if (!grouped[classe]) grouped[classe] = [];
  grouped[classe].push(row);
});
var classes = Object.keys(grouped).sort(function(a, b) { return parseInt(a) - parseInt(b); }).map(function(ck) {
  var classe = parseInt(ck);
  var comptes = grouped[classe].map(function(row) {
    var d = parseFloat(row.total_debit) || 0;
    var c = parseFloat(row.total_credit) || 0;
    var solde = d - c;
    return { numeroCompte: row.numero_compte, libelleCompte: row.compte_libelle || '', totalDebit: d, totalCredit: c, soldeDebiteur: solde > 0 ? solde : 0, soldeCrediteur: solde < 0 ? Math.abs(solde) : 0 };
  });
  var t = comptes.reduce(function(a, co) { return { totalDebit: a.totalDebit + co.totalDebit, totalCredit: a.totalCredit + co.totalCredit, totalSoldeDebiteur: a.totalSoldeDebiteur + co.soldeDebiteur, totalSoldeCrediteur: a.totalSoldeCrediteur + co.soldeCrediteur }; }, { totalDebit: 0, totalCredit: 0, totalSoldeDebiteur: 0, totalSoldeCrediteur: 0 });
  return { classe: classe, libelleClasse: classLabels[classe] || '', comptes: comptes, totalDebit: t.totalDebit, totalCredit: t.totalCredit, totalSoldeDebiteur: t.totalSoldeDebiteur, totalSoldeCrediteur: t.totalSoldeCrediteur };
});
var tg = classes.reduce(function(a, cd) { return { totalDebit: a.totalDebit + cd.totalDebit, totalCredit: a.totalCredit + cd.totalCredit, totalSoldeDebiteur: a.totalSoldeDebiteur + cd.totalSoldeDebiteur, totalSoldeCrediteur: a.totalSoldeCrediteur + cd.totalSoldeCrediteur }; }, { totalDebit: 0, totalCredit: 0, totalSoldeDebiteur: 0, totalSoldeCrediteur: 0 });
return [{ json: { success: true, classes: classes, totauxGeneraux: tg } }];
