const body = $input.first().json.body || $input.first().json;
if (!body.tenantId) throw new Error('tenantId is required');
if (!body.exerciceId) throw new Error('exerciceId is required');
return [{ json: { tenantId: body.tenantId, exerciceId: body.exerciceId, confirme: body.confirme === true } }];

const exerciceArr = $('05. Get Exercice').all().map(i => i.json).filter(e => e.id);
if (exerciceArr.length === 0) throw new Error('Exercice non trouvé ou déjà clôturé');
const exercice = exerciceArr[0];

const nbBrouillon = parseInt($('06. Check Brouillons').first().json.nb_brouillon) || 0;
const calc = $('07. Calc Resultat').first().json;
const p = $('02. Parser').first().json;

const totalDebit = parseFloat(calc.total_debit) || 0;
const totalCredit = parseFloat(calc.total_credit) || 0;
const totalProduits = parseFloat(calc.total_produits) || 0;
const totalCharges = parseFloat(calc.total_charges) || 0;
const resultatNet = Math.round((totalProduits - totalCharges) * 100) / 100;
const equilibre = Math.abs(totalDebit - totalCredit) < 0.01;

const avertissements = [];
if (nbBrouillon > 0) avertissements.push(`${nbBrouillon} écriture(s) en brouillon non validée(s)`);
if (!equilibre) avertissements.push(`Balance déséquilibrée: débit ${totalDebit} ≠ crédit ${totalCredit}`);

return [{ json: {
  tenantId: p.tenantId,
  exerciceId: p.exerciceId,
  confirme: p.confirme,
  exercice: { code: exercice.code, dateDebut: exercice.date_debut, dateFin: exercice.date_fin },
  resultatNet,
  benefice: resultatNet >= 0,
  totalProduits,
  totalCharges,
  equilibre,
  nbBrouillon,
  avertissements,
  peutCloturer: equilibre && nbBrouillon === 0
} }];

const check = $('08. Pre-Cloture Check').first().json;
if (check.confirme && check.peutCloturer) {
  const clot = $('10. Cloturer').first().json;
  return [{ json: { success: true, message: `Exercice ${clot.code} clôturé. Résultat net: ${clot.resultat_net}€`, resultatNet: parseFloat(clot.resultat_net), cloture: true } }];
} else {
  return [{ json: {
    success: true,
    cloture: false,
    message: check.peutCloturer ? 'Prêt à clôturer. Renvoyez avec confirme=true pour valider.' : 'Impossible de clôturer.',
    verification: {
      resultatNet: check.resultatNet,
      benefice: check.benefice,
      equilibre: check.equilibre,
      nbBrouillon: check.nbBrouillon,
      avertissements: check.avertissements,
      peutCloturer: check.peutCloturer
    }
  } }];
}
