var body = $input.first().json.body || $input.first().json;
if (!body.tenantId) throw new Error('tenantId is required');
var UUID_RE = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!UUID_RE.test(body.tenantId)) throw new Error('tenantId invalide');
var today = new Date().toISOString().split('T')[0];
return [{ json: { tenantId: body.tenantId, dateFrom: body.dateFrom || null, dateTo: body.dateTo || today } }];

function esc(v) { return "'" + String(v).replace(/'/g, "''") + "'"; }
var p = $('Parser').first().json;
var dateFilter = '';
if (p.dateFrom) dateFilter += ' AND e.date_ecriture >= ' + esc(p.dateFrom) + '::date';
if (p.dateTo) dateFilter += ' AND e.date_ecriture <= ' + esc(p.dateTo) + '::date';
var query = "SELECT le.numero_compte, pc.libelle, pc.classe, CASE WHEN pc.classe = 6 THEN COALESCE(SUM(le.debit - le.credit), 0) WHEN pc.classe = 7 THEN COALESCE(SUM(le.credit - le.debit), 0) ELSE 0 END as montant FROM lignes_ecritures le JOIN ecritures_comptables e ON le.ecriture_id = e.id LEFT JOIN plan_comptable pc ON le.numero_compte = pc.numero_compte AND pc.tenant_id = e.tenant_id WHERE e.tenant_id = " + esc(p.tenantId) + "::uuid AND e.statut = 'validee' AND pc.classe IN (6, 7)" + dateFilter + " GROUP BY le.numero_compte, pc.libelle, pc.classe HAVING COALESCE(SUM(CASE WHEN pc.classe = 6 THEN le.debit - le.credit WHEN pc.classe = 7 THEN le.credit - le.debit ELSE 0 END), 0) != 0 ORDER BY le.numero_compte";
return [{ json: { query: query, dateFrom: p.dateFrom, dateTo: p.dateTo } }];

var items = $('Query CR').all().map(function(i) { return i.json; }).filter(function(r) { return r.numero_compte; });
var p = $('Build Queries').first().json;
var produitsExploitation = [], produitsFinanciers = [], produitsExceptionnels = [];
var chargesExploitation = [], chargesFinancieres = [], chargesExceptionnelles = [];
var totalProduits = 0, totalCharges = 0;
items.forEach(function(row) {
  var poste = { numero: row.numero_compte, libelle: row.libelle || 'Sans libellé', montant: parseFloat(row.montant) || 0 };
  var np = row.numero_compte.substring(0, 2);
  if (parseInt(row.classe) === 7) {
    totalProduits += poste.montant;
    if (np >= '70' && np <= '75') produitsExploitation.push(poste);
    else if (np === '76') produitsFinanciers.push(poste);
    else if (np === '77') produitsExceptionnels.push(poste);
  } else if (parseInt(row.classe) === 6) {
    totalCharges += poste.montant;
    if (np >= '60' && np <= '65') chargesExploitation.push(poste);
    else if (np === '66') chargesFinancieres.push(poste);
    else if (np === '67') chargesExceptionnelles.push(poste);
  }
});
var resultatNet = totalProduits - totalCharges;
return [{ json: {
  success: true,
  produits: { produitsExploitation: produitsExploitation, produitsFinanciers: produitsFinanciers, produitsExceptionnels: produitsExceptionnels, totalProduits: totalProduits },
  charges: { chargesExploitation: chargesExploitation, chargesFinancieres: chargesFinancieres, chargesExceptionnelles: chargesExceptionnelles, totalCharges: totalCharges },
  resultatExploitation: totalProduits - totalCharges,
  resultatNet: resultatNet,
  periode: { dateFrom: p.dateFrom || '', dateTo: p.dateTo || '' }
} }];
