var body = $input.first().json.body || $input.first().json;
if (!body.tenantId) throw new Error('tenantId is required');
return [{ json: { tenantId: body.tenantId, dateFrom: body.dateFrom || null, dateTo: body.dateTo || null } }];

var items = $('05. Query CR').all().map(function(i) { return i.json; }).filter(function(c) { return c.numero_compte; });

var charges = { exploitation: [], financieres: [], exceptionnelles: [], total: 0 };
var produits = { exploitation: [], financiers: [], exceptionnels: [], total: 0 };

for (var c of items) {
  var montant = Math.round((parseFloat(c.montant) || 0) * 100) / 100;
  var entry = { numeroCompte: c.numero_compte, libelle: c.libelle, montant };
  var prefix = c.numero_compte.substring(0, 2);

  if (c.classe === 6) {
    if (['66'].includes(prefix)) { charges.financieres.push(entry); }
    else if (['67'].includes(prefix)) { charges.exceptionnelles.push(entry); }
    else { charges.exploitation.push(entry); }
    charges.total += montant;
  } else if (c.classe === 7) {
    if (['76'].includes(prefix)) { produits.financiers.push(entry); }
    else if (['77'].includes(prefix)) { produits.exceptionnels.push(entry); }
    else { produits.exploitation.push(entry); }
    produits.total += montant;
  }
}

charges.total = Math.round(charges.total * 100) / 100;
produits.total = Math.round(produits.total * 100) / 100;

// SIG - Soldes IntermÃ©diaires de Gestion
var chargesExpl = charges.exploitation.reduce(function(s, c) { return s + c.montant; }; }, 0);
var produitsExpl = produits.exploitation.reduce(function(s, p) { return s + p.montant; }; }, 0);
var chargesFin = charges.financieres.reduce(function(s, c) { return s + c.montant; }; }, 0);
var produitsFin = produits.financiers.reduce(function(s, p) { return s + p.montant; }; }, 0);
var chargesExc = charges.exceptionnelles.reduce(function(s, c) { return s + c.montant; }; }, 0);
var produitsExc = produits.exceptionnels.reduce(function(s, p) { return s + p.montant; }; }, 0);

var resultatExploitation = Math.round((produitsExpl - chargesExpl) * 100) / 100;
var resultatFinancier = Math.round((produitsFin - chargesFin) * 100) / 100;
var resultatCourant = Math.round((resultatExploitation + resultatFinancier) * 100) / 100;
var resultatExceptionnel = Math.round((produitsExc - chargesExc) * 100) / 100;
var resultatNet = Math.round((produits.total - charges.total) * 100) / 100;

return [{ json: {
  success: true,
  compteResultat: {
    charges,
    produits,
    sig: {
      resultatExploitation,
      resultatFinancier,
      resultatCourant,
      resultatExceptionnel,
      resultatNet
    },
    benefice: resultatNet >= 0
  }
} }];
