var body = $input.first().json.body || $input.first().json;
if (!body.tenantId) throw new Error('tenantId is required');
return [{ json: { tenantId: body.tenantId } }];

var stats = $('05. Stats Generales').first().json;
var fin = $('06. Stats Financieres').first().json;
var evolution = $('07. Evolution Mensuelle').all().map(function(i) { return i.json; }).filter(function(e) { return e.mois; });

var totalProduits = Math.round((parseFloat(fin.total_produits) || 0) * 100) / 100;
var totalCharges = Math.round((parseFloat(fin.total_charges) || 0) * 100) / 100;
var resultatNet = Math.round((totalProduits - totalCharges) * 100) / 100;
var tresorerie = Math.round((parseFloat(fin.tresorerie) || 0) * 100) / 100;
var creancesClients = Math.round((parseFloat(fin.creances_clients) || 0) * 100) / 100;
var dettesFournisseurs = Math.round((parseFloat(fin.dettes_fournisseurs) || 0) * 100) / 100;
var tvaCollectee = Math.round((parseFloat(fin.tva_collectee) || 0) * 100) / 100;
var tvaDeductible = Math.round((parseFloat(fin.tva_deductible) || 0) * 100) / 100;
var tvaDue = Math.round((tvaCollectee - tvaDeductible) * 100) / 100;
var bfr = Math.round((creancesClients - dettesFournisseurs) * 100) / 100;
var margeNette = totalProduits > 0 ? Math.round((resultatNet / totalProduits) * 10000) / 10000 : 0;

return [{ json: {
  success: true,
  kpis: {
    chiffreAffaires: totalProduits,
    totalProduits: totalProduits,
    totalCharges: totalCharges,
    resultatNet: resultatNet,
    margeNette: margeNette,
    tresorerie: tresorerie,
    bfr: bfr,
    ratioEndettement: totalProduits > 0 ? Math.round((dettesFournisseurs / totalProduits) * 10000) / 10000 : 0,
    creancesClients: creancesClients,
    dettesFournisseurs: dettesFournisseurs
  },
  evolutionMensuelle: evolution.map(function(e) { return ({
    mois: e.mois,
    produits: Math.round((parseFloat(e.produits) || 0) * 100) / 100,
    charges: Math.round((parseFloat(e.charges) || 0) * 100) / 100,
    resultat: Math.round(((parseFloat(e.produits) || 0) - (parseFloat(e.charges) || 0)) * 100) / 100
  }); }),
  tva: { collectee: tvaCollectee, deductible: tvaDeductible, due: tvaDue > 0 ? tvaDue : 0, credit: tvaDue < 0 ? Math.abs(tvaDue) : 0 },
  alertes: [],
  exerciceCourant: stats.exercice_courant,
  stats: {
    totalEcritures: parseInt(stats.total_ecritures) || 0,
    ecrituresBrouillon: parseInt(stats.ecritures_brouillon) || 0,
    ecrituresValidees: parseInt(stats.ecritures_validees) || 0,
    nbComptes: parseInt(stats.nb_comptes) || 0
  }
} }];
